import React from 'react';
import './InfoTab.css';

const InfoTab = ({ match }) => {
  if (!match) return null;

  const {
    matchType,
    tossWinner,
    tossChoice,
    date,
    venue,
    status,
  } = match;

  const formattedDate = date
    ? new Date(date).toLocaleString()
    : 'N/A';

  return (
    <div className="info-tab">
      <table className="info-table">
        <tbody>
          {matchType && (
            <tr>
              <td><strong>Series</strong></td>
              <td>{matchType.toUpperCase()}</td>
            </tr>
          )}
          {matchType && (
            <tr>
              <td><strong>Match Type</strong></td>
              <td>{matchType.toUpperCase()}</td>
            </tr>
          )}
          {formattedDate && (
            <tr>
              <td><strong>Date</strong></td>
              <td>{formattedDate}</td>
            </tr>
          )}
          {venue && (
            <tr>
              <td><strong>Venue</strong></td>
              <td>{venue}</td>
            </tr>
          )}
          {(tossWinner && tossChoice) && (
            <tr>
              <td><strong>Toss</strong></td>
              <td>{tossWinner} won the toss and elected to {tossChoice} first</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default InfoTab;
