import React, { useEffect, useState } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import InfoTab from './InfoTab';
import ScorecardTab from './ScorecardTab';
import LineupTab from './LineupTab';
import CommentaryTab from './CommentaryTab';
import FantasyTab from './FantasyTab';
import NewsTab from './NewsTab';
import SeriesPointsTab from './SeriesPointsTab';
import './css/MatchDetails.css';

const MatchDetails = () => {
  const { id } = useParams();
  const [matchDetails, setMatchDetails] = useState(null);
  const [matchLineup, setMatchLineup] = useState(null);
  const [matchScorecard, setMatchScorecard] = useState(null);
  const [matchCommentary, setMatchCommentary] = useState(null);
  const [fantasyPoints, setFantasyPoints] = useState(null);
  const [activeTab, setActiveTab] = useState('info');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMatchDetails = async () => {
      try {
        const res = await fetch(`/api/match/${id}`);
        const json = await res.json();
        setMatchDetails(json.data);
      } catch (err) {
        console.error('Error fetching match details', err);
      } finally {
        setLoading(false);
      }
    };

    fetchMatchDetails();
  }, [id]);

  useEffect(() => {
    const fetchMatchScorecard = async () => {
      try {
        const res = await fetch(`/api/match/scorecard/${id}`);
        const json = await res.json();
        setMatchScorecard(json.data);
      } catch (err) {
        console.error('Error fetching match scorecard', err);
      } finally {
        setLoading(false);
      }
    };

    fetchMatchScorecard();
  }, [id]);

  useEffect(() => {
    const fetchMatchLineup = async () => {
      try {
        const res = await fetch(`/api/match/lineup/${id}`);
        const json = await res.json();
        setMatchLineup(json.data);
      } catch (err) {
        console.error('Error fetching match lineup', err);
      } finally {
        setLoading(false);
      }
    };

    fetchMatchLineup();
  }, [id]);

  useEffect(() => {
    const fetchMatchCommentary = async () => {
      try {
        const res = await fetch(`/api/match/commentary/${id}`);
        const json = await res.json();
        setMatchCommentary(json?.data?.bbb || []);
      } catch (err) {
        console.error('Error fetching commentary', err);
      }
    };

    fetchMatchCommentary();
  }, [id]);

  useEffect(() => {
    const fetchFantasyPoints = async () => {
      try {
        const res = await fetch(`/api/match/fantasy/${id}`);
        const json = await res.json();
        setFantasyPoints(json.data || []);
      } catch (err) {
        console.error('Error fetching fantasy points', err);
      }
    };
    fetchFantasyPoints();
  }, [id]);

  if (loading) return <div>Loading match details...</div>;
  if (!matchDetails) return <div>Loading match details...</div>;

  const renderTabContent = () => {
    switch (activeTab) {
      case 'info':
        return <InfoTab match={matchDetails} seriesId={matchDetails.series_id}/>;
      case 'scorecard':
        return <ScorecardTab match={matchScorecard} commentary={matchCommentary} />;
      case 'lineup':
        return <LineupTab match={matchLineup} />;
      case 'commentary':
        return <CommentaryTab commentary={matchCommentary} scorecard={matchScorecard?.scorecard || []} />;
      case 'fantasy':
        return <FantasyTab fantasyData={fantasyPoints} />;
      case 'pointstable':
        return <SeriesPointsTab seriesId={matchDetails.series_id} />;
      case 'news':
        return <NewsTab matchName={matchDetails.name} />;
      default:
        return null;
    }
  };

  return (
    <div className="match-details">
      <div className="tabs">
        <button onClick={() => setActiveTab('info')} className={activeTab === 'info' ? 'active' : ''}>Info</button>
        <button onClick={() => setActiveTab('scorecard')} className={activeTab === 'scorecard' ? 'active' : ''}>Scorecard</button>
        <button onClick={() => setActiveTab('lineup')} className={activeTab === 'lineup' ? 'active' : ''}>Squads</button>
        <button onClick={() => setActiveTab('commentary')} className={activeTab === 'commentary' ? 'active' : ''}>Ball-by-Ball</button>
        <button onClick={() => setActiveTab('fantasy')} className={activeTab === 'fantasy' ? 'active' : ''}>Fantasy Match Points</button>
        <button onClick={() => setActiveTab('pointstable')} className={activeTab === 'pointstable' ? 'active' : ''}>Points Table</button>
        <button onClick={() => setActiveTab('news')} className={activeTab === 'news' ? 'active' : ''}>News</button>
      </div>

      <div className="tab-content">
        {renderTabContent()}
      </div>
    </div>
  );
};

export default MatchDetails;
