import React, { useEffect, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './css/FeaturedMatches.css';

const FeaturedMatches = ({ navigate }) => {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [fallbackImg, setFallbackImg] = useState(null);
  const location = useLocation();

useEffect(() => {
  const fetchMatches = async () => {
    try {
      const res = await fetch('/api/live');
      const json = await res.json();
      const data = json?.data || [];

      const liveMatches = data.filter(
        m => m.matchStarted && !m.matchEnded && m.status !== 'Match not started'
      );

      const upcomingMatches = data.filter(
        m => m.status === 'Match not started'
      );

      const completedMatches = data.filter(
        m => m.matchStarted && m.matchEnded
      );

      const finalMatches = [];

      // Try to add one of each type
      if (liveMatches.length > 0) finalMatches.push(liveMatches[0]);
      if (upcomingMatches.length > 0) finalMatches.push(upcomingMatches[0]);
      if (completedMatches.length > 0) finalMatches.push(completedMatches[0]);

      // Fill remaining slots from leftover matches
      const allLeftover = [
        ...liveMatches.slice(1),
        ...upcomingMatches.slice(1),
        ...completedMatches.slice(1),
      ];

      while (finalMatches.length < 3 && allLeftover.length > 0) {
        finalMatches.push(allLeftover.shift());
      }

      setMatches(finalMatches);
    } catch (err) {
      console.error('Error fetching matches:', err);
    } finally {
      setLoading(false);
    }
  };

  fetchMatches();
}, []);

  useEffect(() => {
    const fetchFallback = async () => {
      try {
        const res = await fetch('/api/default-images');
        const text = await res.text();
        const match = text.match(/flag_url:\s*['"]([^'"]+)['"]/);
        if (match) setFallbackImg(match[1]);
      } catch (err) {
        console.error('Failed to fetch fallback image:', err);
      }
    };

    fetchFallback();
  }, []);

  if (loading) return <p>Loading live matches...</p>;
  if (!matches.length) return <p>No live matches found.</p>;

  return (
    <>
      <div className="live-matches-header">
        <h3>üèè Top Matches</h3>
        <a href="/schedule">View All</a>
      </div>

      <div className="match-cards-row">
        {matches.map(match => (
          <div key={match.id} className="match-card" onClick={() => navigate(`/match/${match.id}`, { state: { from: 'Home' } })}>
            <div className="series-line">
              <span>{match.seriesName}</span>
            </div>

            <div className="match-meta">
              <span>{match.name}</span>
            </div>

            <div className="match-meta">
              <span className="venue">{match.venue}</span>
            </div>

            {match.teamInfo?.map((team, idx) => {
              const innings = match.score?.filter(s => s.inning?.toLowerCase().includes(team.name.toLowerCase())) || [];
              return (
                <div className="team-score-row" key={team.name}>
                  <div className="team-name">
                    <img
                      src={
                        team.img && team.img.includes('h.cricapi.com')
                          ? fallbackImg || ''
                          : team.img
                      }
                      alt={team.name}
                    />

                    <span>
                      {(team.name?.split(' ').length >= 3)
                        ? team.name.split(' ').map(w => w[0]).join('').toUpperCase()
                        : team.name.slice(0, 3).toUpperCase()}
                    </span>
                  </div>
                  <div className="score-text">
                    {innings.map((inn, i) => (
                      <span key={i}>
                        {inn.r}/{inn.w} ({inn.o})
                        {i < innings.length - 1 && ' & '}
                      </span>
                    ))}
                  </div>
                </div>
              );
            })}

            <p className="status">{match.status}</p>

            {match.series_id && (
              <Link
                to={`/series/${match.series_id}`}
                className="series-link"
                onClick={(e) => e.stopPropagation()}
              >
                {match.seriesName || 'Series'}
              </Link>
            )}
          </div>
        ))}
      </div>
    </>
  );
};

export default FeaturedMatches;
