import React, { useEffect, useState } from 'react';
import './Series.css';

export default function Series() {
  const [fixtures, setFixtures] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadFixtures = async () => {
      try {
        const res = await fetch('/api/fixtures');
        const data = await res.json();
        setFixtures(data.data || []);
      } catch (err) {
        console.error('❌ Failed to fetch fixtures:', err);
      } finally {
        setLoading(false);
      }
    };
    loadFixtures();
  }, []);

  return (
    <div className="series-container">
      <h2>Upcoming Series</h2>
      {loading ? (
        <p>Loading fixtures...</p>
      ) : fixtures.length === 0 ? (
        <p>No upcoming matches found.</p>
      ) : (
        <div className="series-grid">
          {fixtures.map(fix => (
            <div className="fixture-card" key={fix.id}>
              <div className="fixture-header">
                <img src={fix.localteam.image_path} alt={fix.localteam.name} className="team-logo" />
                <span>vs</span>
                <img src={fix.visitorteam.image_path} alt={fix.visitorteam.name} className="team-logo" />
              </div>
              <h4>{fix.localteam.name} vs {fix.visitorteam.name}</h4>
              <p><strong>League:</strong> {fix.league?.name}</p>
              <p><strong>Date:</strong> {new Date(fix.starting_at).toLocaleString()}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
