import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Players = () => {
  const [players, setPlayers] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetch(`/api/players`) // your backend route that proxies to https://cricket.sportmonks.com/api/v2.0/players
      .then(res => res.json())
      .then(json => {
        setPlayers(json.data.slice(0, 30)); // first 30 players
        setLoading(false);
      });
  }, []);

  if (loading) return <div>Loading players...</div>;

  return (
    <div className="players-list">
      {players.map(p => (
        <div key={p.id} className="player-card" onClick={() => navigate(`/players/${p.id}`)}>
          <img src={p.image_path} alt={p.fullname} className="player-img" />
          <h4>{p.fullname}</h4>
          <p>{p.position?.name || 'Unknown Role'}</p>
        </div>
      ))}
    </div>
  );
};

export default Players;
