import React from 'react';

const ScorecardTab = ({ match }) => {
  const scoreboards = match?.scoreboards || [];
  const batting = match?.batting || [];
  const bowling = match?.bowling || [];
  const balls = match?.balls || [];

  const innings = {};

  // Group scoreboards, batting, bowling by innings (S1, S2, etc.)
  scoreboards.forEach(sb => {
    const key = sb.scoreboard;
    if (!innings[key]) innings[key] = { score: {}, extras: {}, batting: [], bowling: [] };
    if (sb.type === 'total') innings[key].score = sb;
    else if (sb.type === 'extra') innings[key].extras = sb;
  });

  batting.forEach(b => {
    const key = b.scoreboard;
    if (innings[key]) innings[key].batting.push(b);
  });

  bowling.forEach(b => {
    const key = b.scoreboard;
    if (innings[key]) innings[key].bowling.push(b);
  });

  const getFallOfWickets = (ballsData, scoreboardKey) => {
    return ballsData
      .filter(ball => ball.score?.name === "Wicket" && ball.scoreboard === scoreboardKey)
      .map(ball => ({
        batsman: ball.batsman?.fullname,
        score: ball.score?.runs || 0,
        over: `${ball.over}.${ball.ball_in_over}`,
      }));
  };

  const renderInnings = (key, index) => {
    const inn = innings[key];
    if (!inn || inn.batting.length === 0) return null;

    const title = `${index + 1}st Innings`;

    const fallOfWickets = getFallOfWickets(balls || [], key);

    return (
      <div key={key} className="innings">
        <h3>{title}</h3>

        <table>
          <thead>
            <tr>
              <th>Batsman</th>
              <th>Runs</th>
              <th>Balls</th>
              <th>4s</th>
              <th>6s</th>
              <th>SR</th>
            </tr>
          </thead>
          <tbody>
            {inn.batting.map(b => (
              <tr key={b.batsman_id}>
                <td>{b.batsman?.fullname}</td>
                <td>{b.score}</td>
                <td>{b.ball}</td>
                <td>{b.four_x}</td>
                <td>{b.six_x}</td>
                <td>{b.rate}</td>
              </tr>
            ))}
            <tr>
              <td><strong>Extras</strong></td>
              <td colSpan="5">
                {Object.entries(inn.extras || {})
                  .filter(([key, val]) => typeof val === 'number' && val > 0)
                  .map(([key, val]) => `${key}: ${val}`).join(', ')}
              </td>
            </tr>
            <tr>
              <td><strong>Total</strong></td>
              <td colSpan="5">
                {inn.score?.total}/{inn.score?.wickets} in {inn.score?.overs} overs
              </td>
            </tr>
          </tbody>
        </table>

        <br />
        <h4>Fall of Wickets</h4>
        {fallOfWickets.length > 0 ? (
          <ul>
            {fallOfWickets.map((wkt, i) => (
              <li key={i}>{wkt.batsman} - {wkt.score} ({wkt.over} ov)</li>
            ))}
          </ul>
        ) : (
          <p>No fall of wicket data available.</p>
        )}

        <br />
        <h4>Bowling</h4>
        <table>
          <thead>
            <tr>
              <th>Bowler</th>
              <th>Overs</th>
              <th>Runs</th>
              <th>Wickets</th>
              <th>Economy</th>
            </tr>
          </thead>
          <tbody>
            {inn.bowling.map(b => (
              <tr key={b.bowler_id}>
                <td>{b.bowler?.fullname}</td>
                <td>{b.overs}</td>
                <td>{b.runs}</td>
                <td>{b.wickets}</td>
                <td>{b.rate}</td>
              </tr>
            ))}
          </tbody>
        </table>

        <hr />
      </div>
    );
  };

  const inningsKeys = Object.keys(innings);

  return (
    <div>
      {inningsKeys.map((key, idx) => renderInnings(key, idx))}
    </div>
  );
};

export default ScorecardTab;
