import React, { useEffect, useState } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import CountdownTimer from './CountdownTimer';
import TeamNews from './TeamNews';
import './css/TeamMatches.css';

export default function TeamMatches() {
  const { id } = useParams();
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [teamNames, setTeamNames] = useState({});
  const [venueData, setVenueData] = useState({});
const [activeTab, setActiveTab] = useState(null);
const [availableTabs, setAvailableTabs] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const res = await axios.get(`/api/teams/${id}/matches`);
        const allMatches = res.data.data.fixtures || [];
        setMatches(allMatches);

        // Extract all team IDs
        const teamIds = new Set();
        allMatches.forEach((m) => {
          teamIds.add(m.localteam_id);
          teamIds.add(m.visitorteam_id);
        });

        const venueIds = new Set();
        allMatches.forEach((m) => {
          venueIds.add(m.venue_id);
        });

        const names = {};
        await Promise.all([...teamIds].map(async (teamId) => {
          try {
            const teamRes = await axios.get(`/api/teams/${teamId}`);
            names[teamId] = teamRes.data.data.data.name;
          } catch (err) {
            names[teamId] = 'Unknown';
          }
        }));

        setTeamNames(names);

        const venues = {};
        await Promise.all([...venueIds].map(async (venueId) => {
          try {
            const venueRes = await axios.get(`/api/venues/${venueId}`);
            venues[venueId] = venueRes.data.data;
          } catch (err) {
            venues[venueId] = 'Unknown';
          }
        }));

        setVenueData(venues);
      } catch (err) {
        console.error('Error loading matches:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchMatches();
  }, [id]);

  const getTeamName = (teamId) => teamNames[teamId] || 'TBD';
  const getVenueName = (venueId) => venueData[venueId].name || '';
  const getVenueCity = (venueId) => venueData[venueId].city || '';

  const live = ['LIVE', '1st Innings', '2nd Innings', '3rd Innings', '4th Innings', 'Stump Day 1', 'Stump Day 2', 'Stump Day 3', 'Stump Day 4', 'Tea Break', 'Lunch', 'Dinner', 'Innings Break', 'Int.', 'Delayed'];
  const upcoming = ['NS', 'Postp.'];
  const completed = ['Finished', 'Cancl', 'Aban.'];

  const liveMatches = matches.filter(m => live.includes(m.status));
  const upcomingMatches = matches.filter(m => upcoming.includes(m.status))
  const completedMatches = matches.filter(m => completed.includes(m.status));

  const filterMatches = (type) => {
    if (type === 'live') return liveMatches;
    if (type === 'upcoming') return upcomingMatches;
    if (type === 'completed') return completedMatches;
  };

  const formattedDate = (input) => {
    if (!input) return 'N/A';
    try {
      return new Date(input).toLocaleString();
    } catch (e) {
      return 'Invalid date';
    }
  };

  const tabMatches = filterMatches(activeTab);

  const tabData = [
    { key: 'live', label: 'Live', length: liveMatches.length },
    { key: 'upcoming', label: 'Upcoming', length: upcomingMatches.length },
    { key: 'completed', label: 'Completed', length: completedMatches.length },
  ];

useEffect(() => {
  const tabs = [];
  if (liveMatches.length > 0) tabs.push('live');
  if (upcomingMatches.length > 0) tabs.push('upcoming');
  if (completedMatches.length > 0) tabs.push('completed');

  setAvailableTabs(tabs);
  if (!activeTab && tabs.length > 0) {
    setActiveTab(tabs[0]);
  }
}, [liveMatches, upcomingMatches, completedMatches]);


  return (
    <div className="team-matches">
      <h2>Matches for {getTeamName(id)} Cricket Team</h2>

      <div className="tabs">
        {finalTabs.map((tab) => (
          <button
            key={tab.key}
            className={activeTab === tab.key ? 'active' : ''}
            onClick={() => setActiveTab(tab.key)}
          >
            {tab.label}
          </button>
        ))}
      </div>

{loading ? (
  <p>Loading matches...</p>
) : (
  <>
    {tabMatches.length === 0 && (
      <p>
        No {activeTab === 'live'
          ? 'live'
          : activeTab === 'upcoming'
          ? 'upcoming'
          : 'completed'} matches available.
      </p>
    )}

    <div className="match-list">
      {tabMatches.map((match) => (
        <div
          key={match.id}
          className="match-card"
          onClick={() => navigate(`/match/${match.id}`, { state: { from: 'Teams' } })}
        >
          <h4>{getTeamName(match.localteam_id)} vs {getTeamName(match.visitorteam_id)}, {match.round}</h4>
          {match.status !== 'NS' && <p>Status: {match.status}</p>}
          <p>Venue: {getVenueName(match.venue_id)}, {getVenueCity(match.venue_id)}</p>
          <CountdownTimer startTime={match.starting_at} />
        </div>
      ))}
    </div>
  </>
)}


    </div>
  );
}
