import React, { useEffect, useState } from 'react';
import './Series.css';

export default function Series() {
  const [leagues, setLeagues] = useState([]);
  const [selectedLeague, setSelectedLeague] = useState(null);
  const [fixtures, setFixtures] = useState([]);
  const [filteredFixtures, setFilteredFixtures] = useState([]);
  const [activeTab, setActiveTab] = useState('fixtures'); // 'fixtures' | 'results'
  const [loading, setLoading] = useState(false);
  const [search, setSearch] = useState('');
  const [teamFilter, setTeamFilter] = useState('');
  const [venueFilter, setVenueFilter] = useState('');

  useEffect(() => {
    fetchLeagues();
  }, []);

  const fetchLeagues = async () => {
    try {
      const res = await fetch('/api/leagues');
      const data = await res.json();
      setLeagues(data.data || []);
    } catch (err) {
      console.error('❌ Failed to load leagues:', err);
    }
  };

  const fetchFixtures = async (league_id) => {
    setLoading(true);
    try {
      const res = await fetch(`/api/fixtures?league_id=${league_id}`);
      const data = await res.json();
      setFixtures(data.data || []);
    } catch (err) {
      console.error('❌ Failed to load fixtures:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleLeagueClick = (league) => {
    setSelectedLeague(league);
    fetchFixtures(league.id);
  };

  const backToLeagues = () => {
    setSelectedLeague(null);
    setFixtures([]);
    setSearch('');
    setTeamFilter('');
    setVenueFilter('');
    setActiveTab('fixtures');
  };

  const groupByDate = (fixtures) => {
    const groups = {};
    fixtures.forEach(fix => {
      const date = fix.starting_at.split('T')[0];
      if (!groups[date]) groups[date] = [];
      groups[date].push(fix);
    });
    return groups;
  };

  // Filter logic
  useEffect(() => {
    const filtered = fixtures.filter(fix => {
      const matchType = fix.status === 'NS' ? 'fixtures' : 'results';
      if (matchType !== activeTab) return false;

      const teamNames = `${fix.localteam?.name || ''} ${fix.visitorteam?.name || ''}`.toLowerCase();
      const venue = fix.venue?.name?.toLowerCase() || '';

      return (
        (!search || teamNames.includes(search.toLowerCase()) || venue.includes(search.toLowerCase())) &&
        (!teamFilter || teamNames.includes(teamFilter.toLowerCase())) &&
        (!venueFilter || venue.includes(venueFilter.toLowerCase()))
      );
    });
    setFilteredFixtures(filtered);
  }, [fixtures, activeTab, search, teamFilter, venueFilter]);

  return (
    <div className="series-container">
      <h2>Series</h2>

      {!selectedLeague ? (
        <div className="leagues-grid">
          {leagues.map(league => (
            <div key={league.id} className="league-card" onClick={() => handleLeagueClick(league)}>
              <h4>{league.name}</h4>
              <p>{league.code}</p>
            </div>
          ))}
        </div>
      ) : (
        <>
          <button className="back-btn" onClick={backToLeagues}>← Back to Leagues</button>
          <h3>{selectedLeague.name}</h3>

          <div className="filters">
            <input
              type="text"
              placeholder="Search by team/venue..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
            <input
              type="text"
              placeholder="Filter by team..."
              value={teamFilter}
              onChange={(e) => setTeamFilter(e.target.value)}
            />
            <input
              type="text"
              placeholder="Filter by venue..."
              value={venueFilter}
              onChange={(e) => setVenueFilter(e.target.value)}
            />
          </div>

          <div className="tab-switch">
            <button
              className={activeTab === 'fixtures' ? 'active' : ''}
              onClick={() => setActiveTab('fixtures')}
            >
              Upcoming Fixtures
            </button>
            <button
              className={activeTab === 'results' ? 'active' : ''}
              onClick={() => setActiveTab('results')}
            >
              Results
            </button>
          </div>

          {loading ? (
            <p>Loading fixtures...</p>
          ) : (
            <div className="fixtures-list">
              {Object.entries(groupByDate(filteredFixtures)).map(([date, matches]) => (
                <div key={date}>
                  <h4 className="fixture-date">{new Date(date).toDateString()}</h4>
                  {matches.map(fix => (
                    <div key={fix.id} className="fixture-card">
                      <span>{fix.localteam?.name} vs {fix.visitorteam?.name}</span>
                      {fix.status !== 'NS' && (
                        <span className="score">{fix.runs?.[0]?.score || '--'} / {fix.runs?.[0]?.wickets || '--'}</span>
                      )}
                      <span className="venue">{fix.venue?.name || ''}</span>
                    </div>
                  ))}
                </div>
              ))}
            </div>
          )}
        </>
      )}
    </div>
  );
}
