// src/components/home/TopSeries.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useSeries } from './../SeriesContext';
import { Link, useNavigate } from 'react-router-dom';
import './css/TopSeries.css'; // Create styling as needed
import dayjs from 'dayjs';

export default function TopSeries() {
  const { seriesList, loading } = useSeries();
  const [seriesDetails, setSeriesList] = useState([]);
  const navigate = useNavigate();

      const now = dayjs();

      const ongoing = seriesList.filter(s =>
        dayjs(s.start_date).isBefore(now) && dayjs(s.end_date).isAfter(now)
      );

      const upcoming = seriesList.filter(s =>
        dayjs(s.start_date).isAfter(now)
      );

      const topOngoing = ongoing.slice(0, 2);
      const topUpcoming = upcoming.slice(0, 4 - topOngoing.length);

  useEffect(() => {
      setSeriesList([...topOngoing, ...topUpcoming]);
  }, []);

  return (
    <div className="top-series-widget">
      <div className="top-series-header">
        <h3>üèè Top Series</h3>
        <a href="/series">View All</a>
      </div>

      {seriesDetails.map(series => (
        <div
          key={series.id}
          className="series-widget-card"
          onClick={() =>
            navigate(`/series/${series.id}/${series.season_id}`, {
              state: {
                seriesLabel: `${series.name} ${series.season}`,
                seriesCode: series.code,
              },
            })
          }
        >
          <h4>{series.name} {series.season}</h4>
          <p>{series.start_date} - {series.end_date}</p>
          <div className="match-format-counts">
            {series.odi_count > 0 && <p>ODIs: {series.odi_count}</p>}
            {series.t10_count > 0 && <p>T10s: {series.t10_count}</p>}
            {series.t20i_count > 0 && <p>T20Is: {series.t20i_count}</p>}
            {series.t20_count > 0 && <p>T20s: {series.t20_count}</p>}
            {series.test_count > 0 && <p>Tests: {series.test_count}</p>}
          </div>
        </div>
      ))}
    </div>
  );
}
