import React from 'react';
import './PartnershipTab.css';

const PartnershipTab = ({ commentary }) => {
  if (!commentary || commentary.length === 0) {
    return <p>No partnership data available.</p>;
  }

  const partnershipsByInning = [];

  commentary.forEach((entry, idx) => {
    const { inning, batsman, extras = 0, runs = 0, dismissal } = entry;

    if (!partnershipsByInning[inning]) {
      partnershipsByInning[inning] = [];
    }

    const currPartnerships = partnershipsByInning[inning];
    const current = currPartnerships[currPartnerships.length - 1];

    if (!current || current.dismissed) {
      // Start a new partnership
      currPartnerships.push({
        batsmen: [batsman?.name || 'Unknown'],
        runs: runs + extras,
        balls: 1,
        dismissed: !!dismissal,
      });
    } else {
      const batName = batsman?.name || 'Unknown';
      if (!current.batsmen.includes(batName)) {
        if (current.batsmen.length < 2) {
          current.batsmen.push(batName);
        }
      }
      current.runs += runs + extras;
      current.balls += 1;
      current.dismissed = !!dismissal;
    }
  });

  return (
    <div className="partnership-tab">
      {partnershipsByInning.map((inningPartnerships, inningIdx) => (
        <div key={inningIdx} className="inning-block">
          <h3>Inning {inningIdx + 1}</h3>
          {inningPartnerships.map((pair, idx) => (
            <div key={idx} className="partnership-card">
              <strong>
                {pair.batsmen.join(' & ')}
              </strong>
              <p>
                {pair.runs} runs ({pair.balls} balls)
              </p>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default PartnershipTab;
