import React, { useState, useEffect } from 'react';
import './../css/NotificationButton.css';

const NotificationButton = ({ match }) => {
  const [enabled, setEnabled] = useState(false);
  const [showDialog, setShowDialog] = useState(false);

  const matchKey = `notif-${match.id}`;

  useEffect(() => {
    const stored = localStorage.getItem(matchKey);
    if (stored === '1') setEnabled(true);
  }, [matchKey]);

  const handleToggle = () => {
    const newValue = !enabled;
    setEnabled(newValue);
    localStorage.setItem(matchKey, newValue ? '1' : '0');

    // Show dialog only if enabling
    if (newValue) {
      setShowDialog(true);
      setTimeout(() => setShowDialog(false), 5000);
    }
  };

  const handleCloseDialog = () => setShowDialog(false);

  return (
    <div className="notif-container">
      <button className="notify-btn" onClick={e => { e.stopPropagation(); handleToggle(); }}>
        {enabled ? 'ðŸ”” On' : 'ðŸ”• Off'}
      </button>

      {showDialog && (
        <div className="notify-dialog-inline">
          <strong>Notifications enabled for</strong><br />
          <strong style={{ fontSize: '16px' }}>
            {match.localteam?.name} vs {match.visitorteam?.name}
          </strong>
          <span className="close-btn" onClick={handleCloseDialog}>Ã—</span>
        </div>
      )}
    </div>
  );
};

export default NotificationButton;
