import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './css/TeamMatches.css';

export default function TeamMatches() {
  const { id } = useParams();
  const [team, setTeam] = useState(null);
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);

  const liveMatchStatuses = [
    '1st Innings', '2nd Innings', '3rd Innings', '4th Innings',
    'Stump Day 1', 'Stump Day 2', 'Stump Day 3', 'Stump Day 4',
    'Innings Break', 'Tea Break', 'Lunch', 'Dinner', 'Int.', 'Delayed',
  ];
  const completedMatchStatuses = ['Finished', 'Cancl', 'Aban.'];
  const upcomingMatchStatuses = ['NS', 'Postp.'];

  useEffect(() => {
    const fetchTeamAndMatches = async () => {
      try {
        const teamRes = await fetch(`/api/teams/${id}`);
        const teamData = await teamRes.json();
        setTeam(teamData.data);
console.log(team);
        const matchRes = await fetch(`/api/teams/${id}/matches`);
        const matchData = await matchRes.json();
        setMatches(matchData.data || []);
      } catch (err) {
        console.error('Error loading team matches:', err);
      } finally {
        setLoading(false);
      }
    };
    fetchTeamAndMatches();
  }, [id]);

  const formatDate = (input) => {
    try {
      return new Date(input).toLocaleString();
    } catch {
      return 'Invalid date';
    }
  };

  const renderMatches = (statusList, label) => {
    const filtered = matches.filter((m) => statusList.includes(m.status));
    if (!filtered.length) return null;

    return (
      <>
        <h3 className="section-label">{label}</h3>
        <div className="team-match-grid">
          {filtered.map((match) => (
            <div key={match.id} className="team-match-card">
              <h4>Match ID: {match.id}</h4>
              <p>{match.status}</p>
              <p>{formatDate(match.starting_at)}</p>
              <p>Round: {match.round}</p>
              <p>Venue: {match.venue?.name || 'N/A'}</p>
            </div>
          ))}
        </div>
      </>
    );
  };

  if (loading) return <p>Loading matches...</p>;
  if (!team) return <p>Team not found.</p>;

  return (
    <div className="team-matches-page">
      <h2>{team.name} Matches</h2>
      <img src={team.image_path} alt={team.name} className="team-logo" />
      {renderMatches(liveMatchStatuses, 'Live Matches')}
      {renderMatches(upcomingMatchStatuses, 'Upcoming Matches')}
      {renderMatches(completedMatchStatuses, 'Completed Matches')}
    </div>
  );
}
