import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const PlayersList = () => {
  const [players, setPlayers] = useState([]);
  const [search, setSearch] = useState('');
  const [filteredPlayers, setFilteredPlayers] = useState([]);
  const [offset, setOffset] = useState(0);
  const [countryCache, setCountryCache] = useState({});

  useEffect(() => {
    fetch(`/api/players?offset=${offset}`).then(res => res.json()).then(data => {
      setPlayers(data.data);
      setFilteredPlayers(data.data);
    });
  }, [offset]);

  useEffect(() => {
    if (search.trim()) {
      const lower = search.toLowerCase();
      setFilteredPlayers(players.filter(p => p.fullname.toLowerCase().includes(lower)));
    } else {
      setFilteredPlayers(players);
    }
  }, [search, players]);

  const getCountryName = async (countryId) => {
    if (countryCache[countryId]) return countryCache[countryId];
    const res = await fetch(`/api/countries/${countryId}`);
    const json = await res.json();
    const name = json?.data?.name || 'Unknown';
    setCountryCache(prev => ({ ...prev, [countryId]: name }));
    return name;
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Players</h2>
      <input
        type="text"
        placeholder="Search by player name..."
        value={search}
        onChange={e => setSearch(e.target.value)}
        style={{ marginBottom: '15px', padding: '8px', width: '250px' }}
      />
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
        {filteredPlayers.map(player => (
          <div key={player.id} style={{ width: '160px', textAlign: 'center' }}>
            <Link to={`/player/${player.id}`}>
              <img src={player.image_path} alt={player.fullname} style={{ width: '100%', borderRadius: '8px' }} />
              <p><strong>{player.fullname}</strong></p>
              <p style={{ fontSize: '0.9em' }}>
                {countryCache[player.country_id] || (
                  <span onClick={() => getCountryName(player.country_id)}>Loading country...</span>
                )}
              </p>
            </Link>
          </div>
        ))}
      </div>
      <div style={{ marginTop: '20px' }}>
        <button onClick={() => setOffset(offset - 30)} disabled={offset === 0}>Previous</button>
        <button onClick={() => setOffset(offset + 30)} style={{ marginLeft: '10px' }}>Next</button>
      </div>
    </div>
  );
};

export default PlayersList;
