import React from 'react';
import './PartnershipTab.css';

const PartnershipTab = ({ commentary }) => {
  if (!commentary || commentary.length === 0) {
    return <p>No commentary available.</p>;
  }

  // Group commentary by innings
  const inningsGroups = {};
  commentary.forEach(ball => {
    const inning = ball.inning;
    if (!inningsGroups[inning]) inningsGroups[inning] = [];
    inningsGroups[inning].push(ball);
  });

  const getPartnerships = (balls) => {
    const partnerships = [];
    let current = {
      batsmen: new Set(),
      runs: 0,
      balls: 0,
      extras: 0,
    };

    balls.forEach((ball, idx) => {
      const batsman = ball.batsman?.name;
      const runs = ball.runs || 0;
      const extras = ball.extras || 0;
      const dismissal = !!ball.dismissal;

      if (batsman) current.batsmen.add(batsman);
      current.runs += runs + extras;
      current.extras += extras;
      current.balls++;

      if (dismissal) {
        const batsmenArr = Array.from(current.batsmen);
        if (batsmenArr.length === 0) return;

        partnerships.push({
          pair: batsmenArr.join(' & '),
          runs: current.runs,
          balls: current.balls,
          extras: current.extras,
        });

        // Reset with non-out batsman (if available)
        const next = {
          batsmen: new Set(),
          runs: 0,
          balls: 0,
          extras: 0,
        };

        const nextBall = balls[idx + 1];
        if (nextBall?.batsman?.name) {
          next.batsmen.add(nextBall.batsman.name);
        }
        current = next;
      }
    });

    return partnerships;
  };

  return (
    <div className="partnership-tab">
      {Object.keys(inningsGroups)
        .sort((a, b) => b - a)
        .map((inningNum) => {
          const balls = inningsGroups[inningNum];
          const partnerships = getPartnerships(balls);
          return (
            <div key={inningNum} className="inning-section">
              <h3>Inning {parseInt(inningNum) + 1}</h3>
              {partnerships.length === 0 ? (
                <p>No partnerships recorded.</p>
              ) : (
                <table className="partnership-table">
                  <thead>
                    <tr>
                      <th>Batting Pair</th>
                      <th>Runs</th>
                      <th>Balls</th>
                      <th>Extras</th>
                    </tr>
                  </thead>
                  <tbody>
                    {partnerships.map((p, idx) => (
                      <tr key={idx}>
                        <td>{p.pair}</td>
                        <td>{p.runs}</td>
                        <td>{p.balls}</td>
                        <td>{p.extras}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </div>
          );
        })}
    </div>
  );
};

export default PartnershipTab;
