import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './SeriesMatches.css';

export default function SeriesMatches() {
  const { id } = useParams();
  const [matches, setMatches] = useState([]);

  useEffect(() => {
    fetch(`/api/series/${id}/matches`)
      .then(res => res.json())
      .then(data => setMatches(data.data || []))
      .catch(console.error);
  }, [id]);

  return (
    <div className="series-matches">
      <h2 className="section-title">ðŸ“… Matches</h2>
      {matches.length === 0 && <p>No matches found.</p>}
      <ul className="match-list">
        {matches.map(match => (
          <li key={match.id} className="match-item">
            {match.localteam?.name} vs {match.visitorteam?.name}
          </li>
        ))}
      </ul>
    </div>
  );
}
