import React, { useState, useEffect } from 'react';
import { useRankings } from './../context/RankingsContext';
import './css/TeamRankings.css';

const TeamRankings = () => {
  const { rankings, loading } = useRankings();
  const [activeTab, setActiveTab] = useState('TEST');

  const getTabLabel = (type) => {
    switch (type) {
      case 'TEST': return 'Test';
      case 'ODI': return 'ODI';
      case 'T20I': return 'T20';
      default: return type;
    }
  };

  const filtered = rankings.find(r => r.type === activeTab);

  if (loading) return <div>Loading...</div>;

  return (
    <div className="icc-ranking-section">
      <div className="ranking-header">
        <h3>üèÜ ICC Team Rankings</h3>
        <a href="/">Back</a>
      </div>

      <div className="tabs">
        {['TEST', 'ODI', 'T20I'].map(type => (
          <button
            key={type}
            className={activeTab === type ? 'active' : ''}
            onClick={() => setActiveTab(type)}
          >
            {getTabLabel(type)}
          </button>
        ))}
      </div>

      {filtered && filtered.team ? (
        <div className="ranking-column">
          <div className="ranking-list">
            {filtered.team
              .filter(t => t.ranking)
              .sort((a, b) => a.ranking.position - b.ranking.position)
              .map((team, index) => (
                <div key={team.id} className="ranking-row">
                  <span className={`pos rank-${index + 1}`}>{team.ranking.position}</span>
                  <img
                    src={team.image_path}
                    alt={team.name}
                    className="flag"
                    onError={(e) => {
                      e.target.src = 'https://em-content.zobj.net/source/apple/354/white-flag_1f3f3-fe0f.png';
                    }}
                  />
                  <span className="team-name">{team.name}</span>
                  <span className="rating">{team.ranking.matches}</span>
                  <span className="rating">{team.ranking.points}</span>
                  <span className="rating">{team.ranking.rating}</span>
                </div>
              ))}
          </div>
        </div>
      ) : (
        <p>Loading rankings...</p>
      )}
    </div>
  );
};

export default TeamRankings;
