import React, { useEffect, useState } from 'react';
import './css/UpcomingSeries.css';

const UpcomingSeries = () => {
  const [upcomingSeries, setUpcomingSeries] = useState([]);

  useEffect(() => {
    const fetchUpcoming = async () => {
      try {
        const res = await fetch(`/api/series`);
        const data = await res.json();
        const allSeries = data?.data || [];

        const tomorrow = new Date();
        tomorrow.setDate(tomorrow.getDate());

        const filtered = allSeries.filter(s => {
          const start = new Date(s.startDate);
          return start > tomorrow;
        });

        const sorted = filtered.sort(
          (a, b) => new Date(a.startDate) - new Date(b.startDate)
        );

        setUpcomingSeries(sorted.slice(0, 4));
      } catch (err) {
        console.error('Failed to load series:', err);
      }
    };

    fetchUpcoming();
  }, []);

  const formatDateRange = (startDate, endDate) => {
    const start = new Date(startDate);
    const end = new Date(endDate);

    const options = { day: 'numeric', month: 'short' };
    const startStr = start.toLocaleDateString('en-GB', options);
    const endStr = end.toLocaleDateString('en-GB', {
      day: 'numeric',
      month: 'short',
      year: 'numeric',
    });

    return `${startStr} to ${endStr}`;
  };

  return (
    <div className="upcoming-series-widget">
      <div className="header">
        <h3>ðŸ“… Upcoming Series</h3>
        <a href="/series">
          View All
        </a>
      </div>

      <div className="series-list">
        {upcomingSeries.map((s) => (
          <div className="series-card" key={s.id}>
            <div className="flags">
              {s.team1_flag && <img src={s.team1_flag} alt="" />}
              {s.team2_flag && <img src={s.team2_flag} alt="" />}
            </div>
            <div className="series-info">
              <p className="series-name">{s.name}</p>
              <p className="series-dates">
                {formatDateRange(s.startDate, s.endDate)}
              </p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UpcomingSeries;
