import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './css/Players.css';

export default function Players() {
  const [players, setPlayers] = useState([]);
  const [sportmonks, setSportmonks] = useState([]);
  const [query, setQuery] = useState('');
  const [offset, setOffset] = useState(0);

  const [playerNames, setPlayerNames] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('/api/default-player-images')
      .then(res => res.text())
      .then(text => {
        const names = text
          .split('\n')
          .map(n => n.trim())
          .filter(Boolean);
        setPlayerNames(names);
      });
  }, []);

  useEffect(() => {
    const loadPlayerData = async () => {
      setLoading(true);
      const results = [];

      for (const name of playerNames) {
        const cacheKey = `top-player-${name.toLowerCase()}`;
        const cached = sessionStorage.getItem(cacheKey);

        if (cached) {
          results.push(JSON.parse(cached));
          continue;
        }

        try {
          const res = await fetch(`/api/players?search=${encodeURIComponent(name)}`);
          const json = await res.json();
          const match = json?.data?.find(p => p.name.toLowerCase() === name.toLowerCase());
          if (match) {
            const player = {
              id: match.id,
              name: match.name,
              image: match.image,
            };
            sessionStorage.setItem(cacheKey, JSON.stringify(player));
            results.push(player);
          }
        } catch (err) {
          console.warn(`Failed to fetch ${name}:`, err.message);
        }
      }

      setPlayers(results);
      setLoading(false);
    };

    if (playerNames.length > 0) {
      loadPlayerData();
    }
  }, [playerNames]);

  const fetchSportmonksPlayers = async () => {
    const res = await fetch('/api/sportmonks/players');
    const data = await res.json();
    setSportmonks(data?.data || []);
  };

  useEffect(() => {
    fetchSportmonksPlayers();
  }, []);

  const getImage = (name) => {
    const match = sportmonks.find(p =>
      p.fullname.toLowerCase().trim() === name.toLowerCase().trim()
    );
    return match?.image_path || 'https://em-content.zobj.net/source/apple/354/bust-in-silhouette_1f464.png';
  };

  return (
    <div className="players-container">
      <h2>Players</h2>
      <input
        type="text"
        placeholder="Search players..."
        value={query}
        onChange={e => {
          setQuery(e.target.value);
          setOffset(0);
        }}
      />

      <div className="player-grid">
        {players.map(p => (
          <Link key={p.id} to={`/player/${p.id}`} className="player-card">
            <h4>{p.name}</h4>
            <p>{p.country}</p>
          </Link>
        ))}
      </div>

      {!query && (
        <div className="paging">
          <button onClick={() => setOffset(o => Math.max(0, o - 50))} disabled={offset === 0}>
            Prev
          </button>
          <button onClick={() => setOffset(o => o + 50)}>Next</button>
        </div>
      )}
    </div>
  );
}
