import React, { useEffect, useState } from 'react';
import './Players.css';

const Players = () => {
  const [players, setPlayers] = useState([]);
  const [displayedPlayers, setDisplayedPlayers] = useState([]);
  const [visibleCount, setVisibleCount] = useState(10);
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [loading, setLoading] = useState(true);

fetch('http://localhost:5050/api/players')
  .then(res => res.json())
  .then(data => {
    const playersData = Array.isArray(data.data) ? data.data : [];
    setPlayers(playersData);
    setDisplayedPlayers(playersData.slice(0, visibleCount));
    setLoading(false);
  });

  useEffect(() => {
    fetch('http://localhost:5050/api/players')
      .then(res => res.json())
      .then(data => {
        const playersData = Array.isArray(data.data) ? data.data : [];
        setPlayers(playersData);
        setDisplayedPlayers(playersData.slice(0, visibleCount));
        setLoading(false);
      });
  }, []);

  useEffect(() => {
    setDisplayedPlayers(players.slice(0, visibleCount));
  }, [visibleCount, players]);

  const handleLoadMore = () => {
    setVisibleCount(prev => prev + 10);
  };

  const handleSearchChange = (e) => {
    const term = e.target.value;
    setSearchTerm(term);

    if (term.length > 1) {
      const filtered = players.filter(player =>
        player.fullname.toLowerCase().includes(term.toLowerCase())
      );
      setSuggestions(filtered.slice(0, 5));
    } else {
      setSuggestions([]);
    }
  };

  const handleSuggestionClick = (name) => {
    setSearchTerm(name);
    setSuggestions([]);
    const result = players.filter(player =>
      player.fullname.toLowerCase() === name.toLowerCase()
    );
    setDisplayedPlayers(result);
    setVisibleCount(result.length);
  };

  return (
    <div className="players-container">
      <h2>All Players</h2>

      <div className="search-wrapper">
        <input
          type="text"
          placeholder="Search player..."
          value={searchTerm}
          onChange={handleSearchChange}
        />
        {suggestions.length > 0 && (
          <ul className="suggestions">
            {suggestions.map((p, i) => (
              <li key={i} onClick={() => handleSuggestionClick(p.fullname)}>
                {p.fullname}
              </li>
            ))}
          </ul>
        )}
      </div>

      {loading ? (
        <p>Loading players...</p>
      ) : (
        <>
          <div className="players-grid">
            {displayedPlayers.map(player => (
              <div key={player.id} className="player-card">
                <img src={player.image_path} alt={player.fullname} />
                <h3>{player.fullname}</h3>
                <p><strong>Born:</strong> {player.dateofbirth}</p>
                <p><strong>Bat:</strong> {player.battingstyle}</p>
                <p><strong>Bowl:</strong> {player.bowlingstyle}</p>
                <p><strong>Position:</strong> {player.position?.name}</p>
              </div>
            ))}
          </div>

          {visibleCount < players.length && (
            <button className="load-more" onClick={handleLoadMore}>
              Load More
            </button>
          )}
        </>
      )}
    </div>
  );
};

export default Players;
