import React, { useState, useEffect } from 'react';
import './TeamRankings.css';

const TeamRankings = () => {
  const [rankings, setRankings] = useState([]);
  const [activeTab, setActiveTab] = useState('TEST');

  useEffect(() => {
    const fetchRankings = async () => {
      try {
        const res = await fetch(`/api/team-rankings`);
        const data = await res.json();
        setRankings(data?.data || []);
      } catch (err) {
        console.error('Error fetching rankings:', err);
      }
    };
    fetchRankings();
  }, []);

  const getTabLabel = (type) => {
    switch (type) {
      case 'TEST': return 'Test';
      case 'ODI': return 'ODI';
      case 'T20I': return 'T20';
      default: return type;
    }
  };

  const filtered = rankings.find(r => r.type === activeTab);

  return (
    <div className="rankings-container">
      <h2 className="rankings-title">Team Rankings</h2>

      <div className="tabs">
        {['TEST', 'ODI', 'T20I'].map(type => (
          <button
            key={type}
            className={activeTab === type ? 'active' : ''}
            onClick={() => setActiveTab(type)}
          >
            {getTabLabel(type)}
          </button>
        ))}
      </div>

      <div className="rankings-table-container">
        {filtered && filtered.team ? (
          <table className="rankings-table">
            <thead>
              <tr>
                <th>Pos</th>
                <th>Team</th>
                <th>Matches</th>
                <th>Points</th>
                <th>Rating</th>
              </tr>
            </thead>
            <tbody>
              {filtered.team
                .filter(t => t.ranking) // skip teams without ranking data
                .sort((a, b) => a.ranking.position - b.ranking.position)
                .map(team => (
                  <tr key={team.id}>
                    <td>{team.ranking.position}</td>
                    <td className="team-name">
                      <img
                        src={
                          team.image_path && team.image_path.endsWith('cdn.sportmonks.com')
                            ? fallbackImg || '' // wait for fallbackImg
                            : team.image_path
                        }
                        alt={team.name}
                      />
                      {team.name}
                    </td>
                    <td>{team.ranking.matches}</td>
                    <td>{team.ranking.points}</td>
                    <td>{team.ranking.rating}</td>
                  </tr>
                ))}
            </tbody>
          </table>
        ) : (
          <p>Loading rankings...</p>
        )}
      </div>
    </div>
  );
};

export default TeamRankings;
