import React, { useEffect, useState } from 'react';
import './Players.css';

const Players = () => {
  const [players, setPlayers] = useState([]);
  const [filteredPlayers, setFilteredPlayers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5050/api/players')
      .then((res) => res.json())
      .then((data) => {
        setPlayers(data.data);
        setFilteredPlayers(data.data);
      })
      .catch((err) => console.error('Error fetching players:', err));
  }, []);

  const handleSearch = (e) => {
    const value = e.target.value;
    setSearchTerm(value);
    if (value.length === 0) {
      setFilteredPlayers(players);
      setSuggestions([]);
    } else {
      const matched = players.filter((player) =>
        player.fullname.toLowerCase().includes(value.toLowerCase())
      );
      setSuggestions(matched.slice(0, 5));
      setFilteredPlayers(matched);
    }
  };

  const selectSuggestion = (name) => {
    setSearchTerm(name);
    setSuggestions([]);
    setFilteredPlayers(players.filter((p) => p.fullname === name));
  };

  return (
    <div className="players-container">
      <h2>All Players</h2>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Search player by name..."
          value={searchTerm}
          onChange={handleSearch}
        />
        {suggestions.length > 0 && (
          <ul className="suggestions-list">
            {suggestions.map((suggestion) => (
              <li
                key={suggestion.id}
                onClick={() => selectSuggestion(suggestion.fullname)}
              >
                {suggestion.fullname}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="players-grid">
        {filteredPlayers.map((player) => (
          <div key={player.id} className="player-card">
            <img src={player.image_path} alt={player.fullname} />
            <h3>{player.fullname}</h3>
            <p><strong>Born:</strong> {player.dateofbirth}</p>
            <p><strong>Gender:</strong> {player.gender === 'm' ? 'Male' : 'Female'}</p>
            <p><strong>Batting:</strong> {player.battingstyle}</p>
            <p><strong>Bowling:</strong> {player.bowlingstyle}</p>
            <p><strong>Position:</strong> {player.position?.name}</p>
            <p><strong>Country:</strong> {player.country?.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Players;
