import React from 'react';
import './PartnershipsTab.css';

const PartnershipsTab = ({ match }) => {
  if (!match || !match.match || !match.match.batting || !match.match.runs || !match.match.lineup) {
    return <p>Loading partnerships...</p>;
  }

  const { batting, runs, lineup } = match.match;

  const getPlayerName = (id) => {
    const player = lineup.find(p => p.id === id);
    return player ? player.fullname || player.firstname : 'N/A';
  };

  // Group batters by innings (using "scoreboard": "S1", "S2", etc.)
  const groupedBatting = batting.reduce((acc, player) => {
    const key = player.scoreboard;
    if (!acc[key]) acc[key] = [];
    acc[key].push(player);
    return acc;
  }, {});

  const inningsOrder = Object.keys(groupedBatting).sort(); // "S1", "S2", etc.

  const renderInningsPartnerships = (scoreboardKey, index) => {
    const players = groupedBatting[scoreboardKey];
    const inningsRun = runs.find(r => r.inning === index + 1);
    const teamName = inningsRun ? getPlayerName(inningsRun.team_id) : `Innings ${index + 1}`;

    // Sort by order of fall of wickets
    const fallSequence = players
      .filter(p => p.fow_score && p.fow_balls)
      .sort((a, b) => a.fow_balls - b.fow_balls);

    const partnerships = [];
    let lastScore = 0;
    for (let i = 0; i < fallSequence.length; i++) {
      const player = fallSequence[i];
      const nextScore = player.fow_score || 0;
      partnerships.push({
        wicket: i + 1,
        players: [getPlayerName(player.player_id)],
        runs: nextScore - lastScore,
        scoreAtFall: nextScore,
      });
      lastScore = nextScore;
    }

    return (
      <div key={scoreboardKey} className="partnership-section">
        <h3>{index + 1}st Innings ({teamName})</h3>
        <table className="partnership-table">
          <thead>
            <tr>
              <th>Wicket</th>
              <th>Partnership Runs</th>
              <th>Batsman(s)</th>
              <th>Score at Fall</th>
            </tr>
          </thead>
          <tbody>
            {partnerships.map(p => (
              <tr key={p.wicket}>
                <td>{p.wicket}</td>
                <td>{p.runs}</td>
                <td>{p.players.join(', ')}</td>
                <td>{p.scoreAtFall}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <div className="partnerships-tab">
      {inningsOrder.map((key, idx) => renderInningsPartnerships(key, idx))}
    </div>
  );
};

export default PartnershipsTab;
