// frontend/src/pages/News.js
import React, { useEffect, useState } from 'react';

export default function News() {
  const [articles, setArticles] = useState([]);
  const API_KEY = 'your_gnews_api_key'; // Replace with your actual GNews API key

  useEffect(() => {
    fetch(`https://gnews.io/api/v4/search?q=cricket&lang=en&max=10&apikey=${API_KEY}`)
      .then(res => res.json())
      .then(data => setArticles(data.articles || []))
      .catch(console.error);
  }, []);

  return (
<div className="news-container">
  <h1 className="news-title">ðŸ“° Latest Cricket News</h1>
  <div className="news-grid">
    {articles.map((article, index) => (
      <div key={index} className="news-card">
        <img src={article.image} alt={article.title} className="news-image" />
        <h2 className="news-heading">{article.title}</h2>
        <p className="news-meta">{article.source.name} â€¢ {new Date(article.publishedAt).toLocaleString()}</p>
        <p className="news-description">{article.description}</p>
        <a href={article.url} target="_blank" rel="noreferrer" className="news-link">Read more â†’</a>
      </div>
    ))}
  </div>
</div>
  );
}
