// src/pages/Players.js
import React, { useState, useEffect } from 'react';
import '../components/Players.css';

const Players = () => {
  const [players, setPlayers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchPlayers = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5050/api/players?limit=10');
      const data = await response.json();
      setPlayers(data.data);
    } catch (error) {
      console.error('Error fetching players:', error);
    }
    setLoading(false);
  };

  const handleSearchChange = async (e) => {
    const value = e.target.value;
    setSearchTerm(value);
    if (value.length >= 2) {
      try {
        const response = await fetch(`http://localhost:5050/api/players/search?name=${value}`);
        const data = await response.json();
        setSuggestions(data.data.slice(0, 10));
      } catch (error) {
        console.error('Error searching players:', error);
      }
    } else {
      setSuggestions([]);
    }
  };

  useEffect(() => {
    fetchPlayers();
  }, []);

  return (
    <div className="players-page">
      <h2>Players</h2>
      <input
        type="text"
        placeholder="Search player by name"
        value={searchTerm}
        onChange={handleSearchChange}
        className="search-input"
      />
      {suggestions.length > 0 && (
        <ul className="suggestions-list">
          {suggestions.map(player => (
            <li key={player.id}>{player.fullname}</li>
          ))}
        </ul>
      )}
      {loading ? (
        <p>Loading players...</p>
      ) : (
        <div className="players-grid">
          {players.map(player => (
            <div className="player-card" key={player.id}>
              <img src={player.image_path} alt={player.fullname} />
              <h3>{player.fullname}</h3>
              <p><strong>DOB:</strong> {player.dateofbirth}</p>
              <p><strong>Batting:</strong> {player.battingstyle}</p>
              <p><strong>Bowling:</strong> {player.bowlingstyle}</p>
              <p><strong>Role:</strong> {player.position?.name}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Players;
