import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './css/PlayerDetails.css';

export default function PlayerDetails() {
  const { id } = useParams();
  const [player, setPlayer] = useState(null);
  const [countryMap, setCountryMap] = useState({});

  useEffect(() => {
    fetch(`/api/player/${id}`)
      .then(res => res.json())
      .then(data => setPlayer(data.data.data));

    fetch('/api/countries')
      .then(res => res.json())
      .then(data => {
        const map = {};
        data.data.forEach(c => {
          map[c.id] = c.name;
        });
        setCountryMap(map);
      });
  }, [id]);

  if (!player) return <div className="player-details">Loading player info...</div>;
  return (
<div className="player-details">
  <div className="player-header">
    <img src={player.image_path} alt={player.fullname} />
    <div>
      <h2>{player.fullname}</h2>
      <p><strong>Role:</strong> {player.position?.name}</p>
      <p><strong>DOB:</strong> {player.dateofbirth}</p>
    </div>
  </div>

  <h3>Batting Career</h3>
  <table className="stats-table">{/* batting rows */}</table>

  <h3>Bowling Career</h3>
  <table className="stats-table">{/* bowling rows */}</table>
</div>
);
}
