import React, { useEffect, useState } from 'react';

export default function NotificationLog() {
  const [logs, setLogs] = useState([]);

  useEffect(() => {
    const allKeys = Object.keys(localStorage);
    const matched = allKeys
      .filter((key) => key.startsWith('notified_') && localStorage.getItem(key) === '1')
      .map((key) => {
        // Remove prefix
        const suffix = key.replace('notified_', ''); // e.g., "match123_10min"
        const parts = suffix.split('_');
        const event = parts.pop(); // last part is event name
        const matchId = parts.join('_'); // rest is matchId (handles underscores safely)
        return { matchId, event };
      });
    setLogs(matched);
  }, []);

  const clearLogs = () => {
    Object.keys(localStorage).forEach(k => {
      if (k.startsWith('notified_')) localStorage.removeItem(k);
    });
    setLogs([]);
  };

  return (
    <div style={{ padding: '1rem', border: '1px solid #ccc', marginTop: '1rem' }}>
      <h3>ðŸ“‹ Notification History</h3>
      {logs.length === 0 ? (
        <p>No notifications triggered yet.</p>
      ) : (
        <ul>
          {logs.map((log, index) => (
            <li key={index}>
              Match <strong>{log.matchId}</strong> â†’ <em>{log.event}</em>
            </li>
          ))}
        </ul>
      )}
      <button onClick={clearLogs} style={{ marginTop: '10px' }}>
        ðŸ”„ Clear Notification History
      </button>
    </div>
  );
}
