import React from 'react';
import { useNavigate } from 'react-router-dom';
import CountdownTimer from './CountdownTimer';
import NotificationButton from './NotificationButton';
import { getTeamRuns, venueName, getTossWinnerName, getTossDecision, matchStageName } from '../utils/matchUtils';
import {
  upcomingMatchStatuses,
  otherMatchStatuses,
} from './../constants/matchStatusConstants';
import './MatchCard1.css';

export default function MatchCard({ match }) {
  const navigate = useNavigate();

  return (
    <div className="match-card" onClick={() => navigate(`/match/${match.id}`)}>
      <div className="match-header-top">
        {match.type && <span className="match-type-tag">{match.type}</span>}
        {upcomingMatchStatuses.includes(match.status) && <NotificationButton match={match} />}
      </div>

      <p>{matchStageName(match)} - {match.round}</p>

      <p>
        <img src={match.localteam.image_path} alt="" className="flag-icon" />
        {match.localteam.name} {getTeamRuns(match, match.localteam.id)}
      </p>

      <p>
        <img src={match.visitorteam.image_path} alt="" className="flag-icon" />
        {match.visitorteam.name} {getTeamRuns(match, match.visitorteam.id)}
      </p>

      {match.status === 'NS' && (
        <CountdownTimer startTime={match.starting_at} offsetMs={600000} />
      )}

      {match.note ? (
        <p className="status">{match.note}</p>
      ) : (
        match.toss_won_team_id && match.elected && (
          <p>{getTossWinnerName(match)} won the toss and elected to {getTossDecision(match)}</p>
        )
      )}

      <div className="match-meta">
        <span className="venue">Venue: {venueName(match)}</span>
      </div>

      <div className="notify-btn">
        {!upcomingMatchStatuses.includes(match.status) &&
         !otherMatchStatuses.includes(match.status) && (
          <button
            onClick={(e) => {
              e.stopPropagation();
              navigate(`/match/${match.id}`);
            }}
          >
            Scorecard
          </button>
        )}

        {match.stage_id && match.season_id && (
          <button
            onClick={(e) => {
              e.stopPropagation();
              navigate(`/series/${match.stage_id}/${match.season_id}`, {
                state: {
                  seriesLabel: matchStageName(match),
                  seriesCode: match?.type,
                },
              });
            }}
          >
            Series
          </button>
        )}
      </div>
    </div>
  );
}
