import React, { useEffect, useState } from 'react';
import './TeamRankings.css';

const TeamRankings = () => {
  const [rankings, setRankings] = useState([]);
  const [format, setFormat] = useState('TEST');

  useEffect(() => {
    const fetchRankings = async () => {
      try {
        const response = await fetch('http://localhost:5050/api/rankings');
        const data = await response.json();
        setRankings(data.data); // <-- directly assign array under "data"
      } catch (error) {
        console.error('Error fetching team rankings:', error);
      }
    };
    fetchRankings();
  }, []);

  const currentRankingBlock = rankings.find(r => r.type === format);
  const teams = currentRankingBlock?.team || [];

  return (
    <div className="team-rankings">
      <h2>Team Rankings</h2>
      <div className="ranking-tabs">
        <button onClick={() => setFormat('TEST')} className={format === 'TEST' ? 'active' : ''}>Test</button>
        <button onClick={() => setFormat('ODI')} className={format === 'ODI' ? 'active' : ''}>ODI</button>
        <button onClick={() => setFormat('T20I')} className={format === 'T20I' ? 'active' : ''}>T20</button>
      </div>
      <table className="ranking-table">
        <thead>
          <tr>
            <th>#</th>
            <th>Team</th>
            <th>Matches</th>
            <th>Points</th>
            <th>Rating</th>
          </tr>
        </thead>
        <tbody>
          {teams.map((team, index) => (
            <tr key={team.id}>
              <td>{team.ranking?.position}</td>
              <td>
                <img src={team.image_path} alt={team.code} className="team-flag" />
                {team.name}
              </td>
              <td>{team.ranking?.matches}</td>
              <td>{team.ranking?.points}</td>
              <td>{team.ranking?.rating}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TeamRankings;
