import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './SeriesStandings.css';

export default function SeriesStandings() {
  const { id } = useParams();
  const [standings, setStandings] = useState([]);

  useEffect(() => {
    fetch(`/api/series/${id}/standings`)
      .then(res => res.json())
      .then(data => setStandings(data.data || []));
  }, [id]);

  return (
    <div className="series-container">
      <h2 className="section-title">Standings</h2>
      {standings.length === 0 ? (
        <div className="no-data">No standings available.</div>
      ) : (
        <table className="standing-table">
          <thead>
            <tr>
              <th>Team</th>
              <th>Played</th>
              <th>Won</th>
              <th>Lost</th>
              <th>Points</th>
            </tr>
          </thead>
          <tbody>
            {standings.map((item, idx) => (
              <tr key={idx}>
                <td>{item.team?.name}</td>
                <td>{item.played}</td>
                <td>{item.won}</td>
                <td>{item.lost}</td>
                <td>{item.points}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
