import React, { useState } from 'react';
import { useRankings } from './../context/RankingsContext';
import './css/TeamRankings.css';

const TeamRankings = () => {
  const { rankings, loading } = useRankings();
  const [activeTab, setActiveTab] = useState('TEST');

  const getTabLabel = (type) => {
    switch (type) {
      case 'TEST': return 'Test';
      case 'ODI': return 'ODI';
      case 'T20I': return 'T20';
      default: return type;
    }
  };

  const filtered = rankings.find(r => r.type === activeTab);

  if (loading) return <div className="loading">Loading...</div>;

  return (
    <div className="team-rankings-wrapper">
      <h2 className="title">üèè ICC Team Rankings</h2>

      <div className="tabs">
        {['TEST', 'ODI', 'T20I'].map(type => (
          <button
            key={type}
            className={`tab-button ${activeTab === type ? 'active' : ''}`}
            onClick={() => setActiveTab(type)}
          >
            {getTabLabel(type)}
          </button>
        ))}
      </div>

      <div className="rankings-table-wrapper">
        <table className="rankings-table">
          <thead>
            <tr>
              <th>Position</th>
              <th>Team</th>
              <th>Matches</th>
              <th>Points</th>
              <th>Rating</th>
            </tr>
          </thead>
          <tbody>
            {filtered?.team
              ?.filter(t => t.ranking)
              ?.sort((a, b) => a.ranking.position - b.ranking.position)
              ?.map((team, idx) => (
                <tr key={team.id}>
                  <td className={`rank-col rank-${idx + 1}`}>{team.ranking.position}</td>
                  <td className="team-name">
                    <img
                      src={team.image_path}
                      alt={team.name}
                      className="flag"
                      onError={(e) => {
                        e.target.src = 'https://em-content.zobj.net/source/apple/354/white-flag_1f3f3-fe0f.png';
                      }}
                    />
                    {team.name}
                  </td>
                  <td>{team.ranking.matches}</td>
                  <td>{team.ranking.points}</td>
                  <td className="rating">{team.ranking.rating}</td>
                </tr>
              ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default TeamRankings;
