import React from 'react';
import './PartnershipTab.css';

const PartnershipTab = ({ commentary }) => {
  if (!Array.isArray(commentary) || commentary.length === 0) {
    return <p>No commentary available to calculate partnerships.</p>;
  }

  const partnerships = [];
  let current = {
    batsmen: [],
    runs: 0,
    balls: 0,
    startIndex: 0,
    dismissal: null,
  };

  for (let i = 0; i < commentary.length; i++) {
    const ball = commentary[i];
    const batter = ball.batsman?.name;
    const runs = ball.runs || 0;
    const extras = ball.extras || 0;
    const isWicket = !!ball.dismissal;

    if (!batter) continue;

    // Add batsman to current pair
    if (current.batsmen.length < 2 && !current.batsmen.includes(batter)) {
      current.batsmen.push(batter);
    }

    current.runs += runs + extras;
    current.balls += 1;

    if (isWicket) {
      current.dismissal = ball.dismissal;
      partnerships.push({ ...current });

      // Start next partnership from the same over
      current = {
        batsmen: [current.batsmen.find(b => b !== batter)],
        runs: 0,
        balls: 0,
        startIndex: i + 1,
        dismissal: null,
      };
    }
  }

  // Add final ongoing partnership if valid
  if (current.batsmen.length === 2) {
    partnerships.push(current);
  }

  return (
    <div className="partnerships">
      <h3>Batting Partnerships</h3>
      {partnerships.length === 0 ? (
        <p>No partnerships found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Partnership</th>
              <th>Runs</th>
              <th>Balls</th>
              <th>Dismissal</th>
            </tr>
          </thead>
          <tbody>
            {partnerships.map((p, idx) => (
              <tr key={idx}>
                <td>{p.batsmen.join(' & ')}</td>
                <td>{p.runs}</td>
                <td>{p.balls}</td>
                <td>{p.dismissal ? p.dismissal : '—'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default PartnershipTab;
