import React from 'react';
import './CommentaryTab.css';

const CommentaryTab = ({ commentary }) => {
  if (!commentary) return <p>No commentary available.</p>;
const capitalizeDismissal = (text) => {
  return text
    .split(' ')
    .map(word =>
      word.length > 1 ? word.charAt(0).toUpperCase() + word.slice(1).toLowerCase() : word.toLowerCase()
    )
    .join(' ');
};

return (
  <div className="commentary-list">
    {commentary.map((entry, idx) => {
      const batsman = entry.batsman?.name || 'Unknown Batsman';
      const bowler = entry.bowler?.name || 'Unknown Bowler';
      const overBall = `${entry.over}.${entry.ball}`;
      const runsText = `${entry.runs} run${entry.runs === 1 ? '' : 's'}`;
      const extrasText = entry.extras ? ` + ${entry.extras} extra${entry.extras === 1 ? '' : 's'}` : '';
      const dismissalText = entry.dismissal ? ` – ${capitalizeDismissal(entry.dismissal)}` : '';

      return (
        <div key={idx} className="commentary-entry">
          <div className="overs">Over {overBall} -</div>
          <div className="details">
            <strong> {bowler}</strong> <em>vs</em> <strong>{batsman}</strong> 
            <strong>{batsman}</strong> <em>vs</em> <strong>{bowler}</strong>: {runsText}{extrasText}{dismissalText}
          </div>
        </div>
      );
    })}
  </div>
);
};

export default CommentaryTab;
