import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import CountdownTimer from './../CountdownTimer';
import NotificationButton from './../NotificationButton';
import './css/SeriesMatchesTab.css';

export default function SeriesMatchesTab() {
  const { seriesId } = useParams(); // supports multiple IDs: "12345,12346"
  const stageIds = seriesId.split(',').map(id => id.trim());
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAllMatches = async () => {
      try {
        const allMatches = [];
console.log(stageIds);
        for (const id of stageIds) {
          const res = await axios.get(`/api/series/${id}/matches`);
          const matchData = res.data.data.data || [];

          allMatches.push(...matchData);
        }

        const sorted = allMatches.sort((a, b) =>
          new Date(a.starting_at) - new Date(b.starting_at)
        );
        setMatches(sorted);
      } catch (err) {
        console.error('Error fetching series matches:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchAllMatches();
  }, [seriesId]);

  const liveStatuses = [
    '1st Innings', '2nd Innings', '3rd Innings', '4th Innings',
    'Stump Day 1', 'Stump Day 2', 'Stump Day 3', 'Stump Day 4',
    'Innings Break', 'Tea Break', 'Lunch', 'Dinner', 'Int.', 'Delayed'
  ];
  const completedStatuses = ['Finished', 'Cancl', 'Aban.'];
  const upcomingStatuses = ['NS', 'Postp.'];

  const getTossWinnerName = (match) => {
    if (match.toss_won_team_id === match.localteam?.id) return match.localteam.name;
    if (match.toss_won_team_id === match.visitorteam?.id) return match.visitorteam.name;
    return '';
  };

  const getTossDecision = (match) => {
    return match.elected === 'batting' ? 'bat first' :
           match.elected === 'bowling' ? 'bowl first' : '';
  };

  const getTeamRuns = (match, teamId) => {
    const innings = match.runs?.filter(r => r.team_id === teamId) || [];
    return innings.map(r => `${r.score}/${r.wickets} (${r.overs} ov)`).join(' & ');
  };

  const venueName = (match) => {
    return match.venue?.name && match.venue?.city
      ? `${match.venue.name}, ${match.venue.city}` : 'TBD';
  };

  return (
    <div className="match-cards-row">
      {loading ? (
        <p>Loading matches...</p>
      ) : matches.length === 0 ? (
        <p>No matches available for this series.</p>
      ) : (
        matches.map(match => (
          <div
            key={match.id}
            className="match-card"
            onClick={() => navigate(`/match/${match.id}`, { state: { from: 'Series' } })}
          >
            <div style={{ marginTop: '10px' }}>
              {!completedStatuses.includes(match.status) && <NotificationButton match={match} />}
            </div>

            <p>
              <img src={match.localteam?.image_path} alt="" className="flag-icon" />
              {match.localteam?.name} {getTeamRuns(match, match.localteam?.id)}
            </p>
            <p>
              <img src={match.visitorteam?.image_path} alt="" className="flag-icon" />
              {match.visitorteam?.name} {getTeamRuns(match, match.visitorteam?.id)}
            </p>

            {match.status === 'NS' && !match?.elected && (
              <CountdownTimer startTime={match.starting_at} />
            )}

            {match.note ? (
              <p className="status">{match.note}</p>
            ) : (
              match?.toss_won_team_id && match?.elected && (
                <p>{getTossWinnerName(match)} won the toss and elected to {getTossDecision(match)}</p>
              )
            )}

            <div className="match-meta">
              <span className="venue">Venue: {venueName(match)}</span>
            </div>

            {!upcomingStatuses.includes(match.status) && (
              <div className="notify-btn">
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    navigate(`/match/${match.id}`);
                  }}
                >
                  Scorecard
                </button>
              </div>
            )}
          </div>
        ))
      )}
    </div>
  );
}
