import React, { useEffect, useState } from 'react';
import './Stadiums.css';

const Stadiums = () => {
  const [venues, setVenues] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filtered, setFiltered] = useState([]);

  useEffect(() => {
    const fetchVenues = async () => {
      try {
        const res = await fetch('/api/venues'); // Proxy this to `https://cricket.sportmonks.com/api/v2.0/venues?api_token=...`
        const json = await res.json();
        setVenues(json.data || []);
        setFiltered(json.data || []);
      } catch (err) {
        console.error('Failed to fetch venues:', err);
      }
    };

    fetchVenues();
  }, []);

  useEffect(() => {
    const term = searchTerm.toLowerCase();
    setFiltered(
      venues.filter(
        (v) =>
          v.name?.toLowerCase().includes(term) ||
          v.city?.toLowerCase().includes(term) ||
          v.country?.toLowerCase().includes(term)
      )
    );
  }, [searchTerm, venues]);

  return (
    <div className="stadiums-container">
      <h2>Stadiums</h2>
      <input
        type="text"
        placeholder="Search by name, city, or country..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="search-input"
      />

      <div className="stadium-list">
        {filtered.map((venue) => (
          <div className="stadium-card" key={venue.id}>
            <img src={venue.image_path} alt={venue.name} />
            <div className="stadium-details">
              <h4>{venue.name}</h4>
              <p><strong>City:</strong> {venue.city}</p>
              <p><strong>Capacity:</strong> {venue.capacity || 'Unknown'}</p>
              <p><strong>Floodlights:</strong> {venue.floodlight ? 'Yes' : 'No'}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Stadiums;
