import React from 'react';
import { Link } from 'react-router-dom';
import './css/LineupTab.css';

const LineupTab = ({ match }) => {
  if (!match || !Array.isArray(match) || match.length < 2) {
    return <p>No Squads available for this match.</p>;
  }

  const [team1, team2] = match;

  const renderPlayers = (players) => {
    return players.map((player) => (
      <li key={player.id}>
        <Link to={`/player/${player.id}`} className="player-link" target="_blank">
          {player.name}
        </Link>
        {player.role?.toLowerCase().includes('captain') && ' (C)'}
        {player.role?.toLowerCase().includes('keeper') && ' (WK)'}
      </li>
    ));
  };

  return (
    <div className="lineup-tab">
      <div className="team-lineup">
        <h3>{team1.teamName}</h3>
        <ul>{renderPlayers(team1.players)}</ul>
      </div>

      <div className="team-lineup">
        <h3>{team2.teamName}</h3>
        <ul>{renderPlayers(team2.players)}</ul>
      </div>
    </div>
  );
};

export default LineupTab;
