import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const MatchDetails = () => {
  const { id } = useParams();
  const [match, setMatch] = useState(null);
  const [tab, setTab] = useState('scorecard');

  useEffect(() => {
    const fetchMatchDetails = async () => {
      try {
        const res = await axios.get(`/api/match/${id}`);
        setMatch(res.data.data);
      } catch (err) {
        console.error('Error fetching match details', err);
      }
    };

    fetchMatchDetails();
  }, [id]);

  if (!match) return <p>Loading match details...</p>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>{match.localteam.name} vs {match.visitorteam.name}</h2>
      <div style={{ marginBottom: '20px' }}>
        <button onClick={() => setTab('scorecard')}>üèè Scorecard</button>
        <button onClick={() => setTab('commentary')}>üéôÔ∏è Commentary</button>
        <button onClick={() => setTab('info')}>üìã Info</button>
      </div>

      {tab === 'scorecard' && (
        <div>
          <h3>Scorecard</h3>
          {match.scoreboards
            .filter(sb => sb.type === 'total')
            .map(sb => {
              const team =
                sb.team_id === match.localteam.id
                  ? match.localteam
                  : match.visitorteam;
              return (
                <p key={sb.id}>
                  <img src={team.image_path} alt={team.name} width="30" style={{ marginRight: 10 }} />
                  {team.name}: {sb.total}/{sb.wickets} in {sb.overs} overs
                </p>
              );
            })}
        </div>
      )}

      {tab === 'commentary' && (
        <div>
          <h3>Commentary</h3>
          <p>(Coming soon - fetch from SportMonks commentary API)</p>
        </div>
      )}

      {tab === 'info' && (
        <div>
          <h3>Match Info</h3>
          <p>Type: {match.type}</p>
          <p>Venue ID: {match.venue_id}</p>
          <p>Status: {match.status}</p>
        </div>
      )}
    </div>
  );
};

export default MatchDetails;
