import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function ScorecardTab({ matchId }) {
  const [data, setData] = useState(null);
  const [activeTab, setActiveTab] = useState(null);

  useEffect(() => {
    axios.get(`/api/match/${matchId}`).then(res => {
      setData(res.data.data);
    });
  }, [matchId]);

  if (!data) return <div>Loading...</div>;

  const batting = data.batting || [];
  const lineup = data.lineup || [];
  const balls = data.balls || [];

  const getTeamIdByScoreboard = (sb) => {
    const entry = batting.find(b => b.scoreboard === sb);
    return entry?.team_id || null;
  };

  const getTeamName = (teamId) => {
    if (teamId === data.localteam_id) return data.localteam?.name || 'Local Team';
    if (teamId === data.visitorteam_id) return data.visitorteam?.name || 'Visitor Team';
    return `Team ${teamId}`;
  };

  const getPlayerName = (id) => {
    const player = lineup.find(p => p.id === id);
    return player?.fullname || 'Unknown';
  };

  const groupedByInning = {};

  balls.forEach(ball => {
    const inning = ball.scoreboard || 'Inning';
    const over = Math.floor(ball.ball) + 1;

    if (!groupedByInning[inning]) groupedByInning[inning] = {};
    if (!groupedByInning[inning][over]) groupedByInning[inning][over] = [];

    groupedByInning[inning][over].push(ball);
  });

  const WICKET_EVENTS = [
    "Clean Bowled",
    "LBW OUT",
    "Catch Out",
    "Catch Out (Sub)",
    "Stump Out",
    "Run Out",
    "Run Out + 1",
    "Run Out + 2",
    "Run Out (Subs)",
    "Retired Out",
    "Hit Wicket",
    "Absent",
    "Absent Hurt",
    "Obstructing the field Out"
  ];

  return (
    <div>
      <h3>Overs Summary</h3>
      {Object.entries(groupedByInning).map(([inning, overs]) => (
        <div key={inning}>
          <h4>{getTeamName(getTeamIdByScoreboard(inning))}</h4>
          {Object.entries(overs).map(([overNum, deliveries]) => {
            const runs = deliveries.reduce((sum, b) => sum + (b.score?.runs || 0), 0);
            const wickets = deliveries.filter(b => b.wicket).length;

            const batsman = getPlayerName(deliveries[0]?.batsman_id);
            const bowler = getPlayerName(deliveries[0]?.bowler_id);

            return (
              <div key={overNum} style={{ marginBottom: '10px' }}>
                <strong>Over {overNum}</strong>: {runs} runs, {wickets} wicket(s) â€” 
                <br />Bowler: {bowler}, Batsman: {batsman}
                <br />Balls: {deliveries.map(b => `${b.score?.runs || 0}`).join(', ')}
              </div>
            );
          })}
        </div>
      ))}
    </div>
  );
}
