// src/components/tabs/LineupTab.js
import React from 'react';
import './LineupTab.css';

const LineupTab = ({ match }) => {
  if (!match || !Array.isArray(match) || match.length < 2) {
    return <p>No lineup data available for this match.</p>;
  }

  const [team1, team2] = match;

  const renderPlayers = (players) => {
    return players.map((player) => (
<li key={player.id}>
  <img src={player.playerImg} alt={player.name} className="player-img" />
  {player.name}
  {player.role && ` – ${player.role}`}
</li>
    ));
  };

  return (
    <div className="lineup-tab">
      <div className="team-lineup">
        <h3>{team1.teamName} Lineup</h3>
        <ul>{renderPlayers(team1.players)}</ul>
      </div>

      <div className="team-lineup">
        <h3>{team2.teamName} Lineup</h3>
        <ul>{renderPlayers(team2.players)}</ul>
      </div>
    </div>
  );
};

export default LineupTab;
