import React from 'react';
import './PartnershipTab.css';

const PartnershipTab = ({ commentary }) => {
  if (!commentary || commentary.length === 0) return <p>No partnership data available.</p>;

  const partnershipsByInning = {};

  commentary.forEach(ball => {
    const inning = ball.inning || 0;
    if (!partnershipsByInning[inning]) {
      partnershipsByInning[inning] = [];
    }

    const lastPartnership = partnershipsByInning[inning][partnershipsByInning[inning].length - 1];

    if (
      !lastPartnership ||
      ball.dismissal ||
      (lastPartnership.batsmen &&
        !lastPartnership.batsmen.includes(ball.batsman?.name))
    ) {
      // Start a new partnership
      partnershipsByInning[inning].push({
        batsmen: [ball.batsman?.name],
        runs: ball.runs || 0,
        balls: 1,
        endedBy: ball.dismissal ? ball.dismissal : null,
      });
    } else {
      // Extend current partnership
      if (!lastPartnership.batsmen.includes(ball.batsman?.name)) {
        lastPartnership.batsmen.push(ball.batsman?.name);
      }
      lastPartnership.runs += ball.runs || 0;
      lastPartnership.balls += 1;
      if (ball.dismissal) lastPartnership.endedBy = ball.dismissal;
    }
  });

  return (
    <div className="partnership-tab">
      {Object.entries(partnershipsByInning)
        .sort((a, b) => b[0] - a[0])
        .map(([inning, partnerships]) => (
          <div key={inning} className="inning-section">
            <h3>Inning {parseInt(inning) + 1}</h3>
            {partnerships.map((p, idx) => (
              <div key={idx} className="partnership-card">
                <p>
                  <strong>Players:</strong>{' '}
                  {p.batsmen.length === 2
                    ? `${p.batsmen[0]} & ${p.batsmen[1]}`
                    : p.batsmen.join(', ')}
                </p>
                <p>
                  <strong>Runs:</strong> {p.runs} | <strong>Balls:</strong>{' '}
                  {p.balls}
                </p>
                {p.endedBy && (
                  <p className="dismissal">
                    <strong>Dismissal:</strong> {p.endedBy}
                  </p>
                )}
              </div>
            ))}
          </div>
        ))}
    </div>
  );
};

export default PartnershipTab;
