import React, { createContext, useContext, useEffect, useState } from 'react';

const TeamsContext = createContext();

export const TeamsProvider = ({ children }) => {
  const [teams, setTeams] = useState([]);
  const [filteredTeams, setFilteredTeams] = useState([]);
  const [topTeams, setTopTeams] = useState([]);
  const [loading, setLoading] = useState(true);

  const DEFAULT_TEAM_ID = 10; // India

  useEffect(() => {
    const fetchTeams = async () => {
      try {
        const res = await fetch('/api/teams');
        const json = await res.json();
        const allTeams = json.data?.data || [];
        setTeams(allTeams);

        // Step 1: Filter national teams
        const nationalTeams = allTeams.filter(team => team.national_team === true);
        setFilteredTeams(nationalTeams);

        // Step 2: Create topTeams with DEFAULT_TEAM first
        const sortedTop = [...nationalTeams].sort((a, b) => {
          if (a.id === DEFAULT_TEAM_ID) return -1;
          if (b.id === DEFAULT_TEAM_ID) return 1;
          return a.name.localeCompare(b.name);
        }).slice(0, 13);

        setTopTeams(sortedTop);
      } catch (err) {
        console.error('Error fetching teams:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchTeams();
  }, []);

  return (
    <TeamsContext.Provider value={{ teams, filteredTeams, topTeams, loading }}>
      {children}
    </TeamsContext.Provider>
  );
};

export const useTeams = () => useContext(TeamsContext);
