import React, { useEffect, useState } from 'react';
import './Home.css';
import { Link } from 'react-router-dom';

const Home = () => {
  const [matches, setMatches] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const response = await fetch(`/api/live`);
        const json = await response.json();
        setMatches(json.data);
      } catch (error) {
        console.error('Error fetching match details', error);
      } finally {
        setLoading(false);  // ‚úÖ stop loading
      } 
    };

    fetchMatches();
  }, []);

  if (loading) {
    return (
      <div style={{ padding: '20px' }}>
        <h2>üèè Live Cricket Scores</h2>
        <p>Loading...</p>
      </div>
    );
  }

  if (!matches || matches.length === 0) {
    return (
      <div style={{ padding: '20px' }}>
        <h2>üèè Live Cricket Scores</h2>
        <p>No live matches at the moment.</p>
      </div>
    );
  }

  return (
    <div className="matchlist-container">
      <h2 className="matchlist-heading">üèè Live Cricket Scores</h2>

      <div className="match-grid">
        {matches.map((match) => (
          <Link key={match.id} to={`/match/${match.id}`} state={{ from: 'home' }} className="match-card-link">
            <div className="match-card">
              <div className="match-meta-top">
                <span className="match-round">{match.round}</span>
                <span className="match-league">{match.league?.name} {match.season?.name}</span>
                <span className="match-type">{match.type}</span>
              </div>

              <div className="teams-score-block">
                {[match.localteam, match.visitorteam].map((team) => {
                  const score = match.scoreboards?.find(sb => sb.type === 'total' && sb.team_id === team.id);
                  return (
                    <div className="team-row" key={team.id}>
                      <img src={team.image_path} className="team-logo" alt={team.name} />
                      <span className="team-code">{team.code}</span>
                        {score ? (
                          <span className="team-score">{score.total}-{score.wickets} ({score.overs})</span>
                        ) : (
                          <span className="team-score">-</span>
                        )}
                    </div>
                  );
                })}
              </div>

              {match.note && (
                <p className="match-note-center">{match.note}</p>
              )}
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Home;
