import React, { useEffect, useState } from 'react';
import { useTeams } from './../../context/TeamsContext';
import './css/TopPlayers.css';

export default function TopPlayers() {
  const { filteredTeams, loading: teamsLoading } = useTeams();
  const [selectedTeamId, setSelectedTeamId] = useState(null);
  const [players, setPlayers] = useState([]);
  const [loadingPlayers, setLoadingPlayers] = useState(false);

  useEffect(() => {
    if (filteredTeams.length > 0 && !selectedTeamId) {
      setSelectedTeamId(filteredTeams[0].id); // Default to first national team
    }
  }, [filteredTeams]);

  useEffect(() => {
    if (!selectedTeamId) return;

    async function fetchPlayers() {
      try {
        setLoadingPlayers(true);
        const res = await fetch(`/api/team-squads/${selectedTeamId}`);
        const squad = (await res.json())?.data?.data?.squad || [];

        const withSeasons = squad.filter(p => p.squad?.season_id);
        const topSeasons = [...new Set(withSeasons.map(p => p.squad.season_id))].sort((a, b) => b - a).slice(0, 7);

        const latestSquad = withSeasons
          .filter(p => topSeasons.includes(p.squad.season_id))
          .reduce((acc, player) => {
            if (!acc.some(p => p.id === player.id)) acc.push(player);
            return acc;
          }, []);

        setPlayers(latestSquad);
      } catch (err) {
        console.error('Failed to fetch players:', err);
      } finally {
        setLoadingPlayers(false);
      }
    }

    fetchPlayers();
  }, [selectedTeamId]);

  return (
    <div className="top-players-container">
      <div className="top-players-header">
        <h2>üèè Trending Players</h2>
      </div>

      <div className="team-pill-row">
        {filteredTeams.map(team => (
          <button
            key={team.id}
            className={`pill ${team.id === selectedTeamId ? 'active' : ''}`}
            onClick={() => setSelectedTeamId(team.id)}
          >
            <img src={team.image_path} alt={team.name} className="flag" />
            {team.name}
          </button>
        ))}
      </div>

      {loadingPlayers ? (
        <p className="top-players-loading">Loading players...</p>
      ) : (
        <div className="player-list">
          {players.map(player => (
            <div key={player.id} className="player-item">
              <img
                src={player.image_path || 'https://i.imgur.com/O1RmJDa.jpg'} // fallback image
                alt={player.fullname}
                className="player-img"
              />
              <button className="player-name-btn">{player.fullname}</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
