// src/components/Teams.js
import React, { useEffect, useState } from 'react';
import './Teams.css';

const Teams = () => {
  const [teams, setTeams] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedTeam, setSelectedTeam] = useState(null);
  const [squad, setSquad] = useState([]);

  useEffect(() => {
    const fetchTeams = async () => {
      try {
        const response = await fetch(`/api/teams`);
        const json = await response.json();
        setTeams(json.data || []);
      } catch (err) {
        console.error('Failed to fetch teams:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchTeams();
  }, []);

  const handleTeamClick = async (teamId) => {
    try {
      const response = await fetch(`/api/teams/${teamId}/squad`);
      const json = await response.json();
      setSquad(json.data || []);
      setSelectedTeam(teamId);
    } catch (err) {
      console.error('Failed to fetch squad:', err);
    }
  };

  const closeModal = () => {
    setSelectedTeam(null);
    setSquad([]);
  };

  if (loading) return <div className="loading">Loading Teams...</div>;

  return (
    <div className="teams-container">
      <h2 className="teams-heading">All Teams</h2>
      <div className="teams-grid">
        {teams.map((team) => (
          <div key={team.id} className="team-card" onClick={() => handleTeamClick(team.id)}>
            <img src={team.logo_url || '/team-placeholder.png'} alt={team.name} className="team-flag" />
            <span className="team-name">{team.name}</span>
          </div>
        ))}
      </div>

      {selectedTeam && (
        <div className="modal">
          <div className="modal-content">
            <button className="close-btn" onClick={closeModal}>Ã—</button>
            <h3>Team Squad</h3>
            {squad.length > 0 ? (
              <ul className="squad-list">
                {squad.map(player => (
                  <li key={player.id}>{player.name} - {player.role}</li>
                ))}
              </ul>
            ) : (
              <p>No squad data available.</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Teams;
