import React, { useState } from 'react';
import InfoTab from './match/InfoTab';
import ScorecardTab from './match/ScorecardTab';
import CommentaryTab from './match/CommentaryTab';
import SquadTab from './match/SquadTab';
import OversSummaryTab from './match/OversSummaryTab';
import NewsTab from './match/NewsTab';

export default function MatchDetailTabs({ matchId }) {
  const [activeTab, setActiveTab] = useState('info');

  const tabs = {
    info: <InfoTab matchId={matchId} />,
    scorecard: <ScorecardTab matchId={matchId} />,
    commentary: <CommentaryTab matchId={matchId} />,
    squad: <SquadTab matchId={matchId} />,
    overs: <OversSummaryTab matchId={matchId} />,
    news: <NewsTab matchId={matchId}/>,
  };

  return (
    <div>
      <div className="tabs">
        {Object.keys(tabs).map((tab) => (
          <button
            key={tab}
            className={activeTab === tab ? 'active' : ''}
            onClick={() => setActiveTab(tab)}
            style={{ marginRight: '10px' }}
          >
            {tab.charAt(0).toUpperCase() + tab.slice(1)}
          </button>
        ))}
      </div>
      <div className="tab-content" style={{ marginTop: '20px' }}>
        {tabs[activeTab]}
      </div>
    </div>
  );
}
