import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import InfoTab from './tabs/InfoTab';
import ScorecardTab from './tabs/ScorecardTab';
import CommentaryTab from './tabs/CommentaryTab';
import LineupTab from './tabs/LineupTab';
import './MatchDetails.css';

const MatchDetails = () => {
  const { id } = useParams();
  const [matchData, setMatchData] = useState(null);
  const [tab, setTab] = useState('info');

  useEffect(() => {
    const fetchMatchDetails = async () => {
      try {
        const response = await fetch(`/api/match/${id}`);
        const json = await response.json();
        setMatchData(json.data);
      } catch (error) {
        console.error('Error fetching match details', error);
      }
    };

    fetchMatchDetails();
  }, [id]);

  if (!matchData) return <div className="loading">Loading match details...</div>;

  console.log("MATCH DATA:", matchData);

  const renderTab = () => {
    switch (tab) {
      case 'info':
        return <InfoTab match={matchData} />;
      case 'scorecard':
        return <ScorecardTab match={matchData} />;
      case 'commentary':
        return <CommentaryTab match={matchData} />;
      case 'lineup':
        return <LineupTab match={matchData} />;
      default:
        return null;
    }
  };

  return (
    <div className="match-details">
      <div className="match-header">
        <img src={matchData.localteam.image_path} alt={matchData.localteam.code} />
        <span>{matchData.localteam.name} vs {matchData.visitorteam.name}</span>
        <img src={matchData.visitorteam.image_path} alt={matchData.visitorteam.code} />
      </div>

      <div className="tabs">
        {['info', 'scorecard', 'commentary', 'lineup'].map((key) => (
          <button
            key={key}
            className={tab === key ? 'active' : ''}
            onClick={() => setTab(key)}
          >
            {key.charAt(0).toUpperCase() + key.slice(1)}
          </button>
        ))}
      </div>

      <div className="tab-content">{renderTab()}</div>
    </div>
  );
};

export default MatchDetails;
