import React, { useEffect, useState } from 'react';
import './Home.css';
import { Link } from 'react-router-dom';

export default function Home() {
  const [matches, setMatches] = useState([]);

  useEffect(() => {
    const fetchLiveMatches = async () => {
      try {
        const res = await fetch('/api/live');
        const json = await res.json();
        setMatches(json.data || []);
      } catch (error) {
        console.error('Error loading live matches:', error);
      }
    };

    fetchLiveMatches();
  }, []);

  if (!matches.length) return <div className="loading">No live matches at the moment.</div>;

  return (
    <div className="matchlist-container">
      <h2>üèè Live Matches</h2>
      <div className="match-grid">
        {matches.map(match => (
          <Link key={match.id} to={`/match/${match.id}`} state={{ from: 'home' }} className="match-card-link">
            <div className="match-card">
              <div className="match-header">
                <h3>{match.teams}</h3>
                <p><strong>{match.date}</strong></p>
                <p>{match.status}</p>
                {match.score && <p><em>{match.score}</em></p>}
              </div>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}
