import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function TeamPlayers({ teamName }) {
  const { id } = useParams();
  const [players, setPlayers] = useState([]);
  const [page, setPage] = useState(0);
  const playersPerPage = 30;

useEffect(() => {
  async function fetchPlayers() {
    try {
      const res = await axios.get(`/api/team-squads/${id}`);
      const allPlayers = res.data.data?.squad || [];
      const playersWithSeason = allPlayers.filter(p => p.squad?.season_id);
      const maxSeasonId = Math.max(...playersWithSeason.map(p => p.squad.season_id));
      const latestSquad = playersWithSeason.filter(p => p.squad.season_id === maxSeasonId);

      setPlayers(latestSquad);
      setPage(0); // reset to first page when team changes
    } catch (err) {
      console.error(`❌ Failed fetching players for ${teamName}`, err);
    }
  }
  fetchPlayers();
}, [id]);

  const currentPlayers = players.slice(page * playersPerPage, (page + 1) * playersPerPage);

  return (
    <div>
      <h3>Squad: {teamName}</h3>
      {currentPlayers.map((player) => (
        <div key={player.id} style={{ marginBottom: '12px' }}>
          – {player.fullname}
        </div>
      ))}

      <div style={{ marginTop: '10px' }}>
        <button onClick={() => setPage(page - 1)} disabled={page === 0}>
          Previous
        </button>
        <span style={{ margin: '0 10px' }}>Page {page + 1}</span>
        <button
          onClick={() => setPage(page + 1)}
          disabled={(page + 1) * playersPerPage >= players.length}
        >
          Next
        </button>
      </div>
    </div>
  );
}
