import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function ScorecardTab({ matchId }) {
  const [data, setData] = useState(null);
  const [activeTab, setActiveTab] = useState(null);

  useEffect(() => {
    axios.get(`/api/match/${matchId}`).then(res => {
      const matchData = res.data.data;
      setData(matchData);

      const scoreboards = matchData.scoreboards || [];
      const inningsList = [...new Set(scoreboards.map(s => s.scoreboard))];

      // Set first tab as active by default
      if (inningsList.length > 0) {
        setActiveTab(inningsList[0]);
      }
    });
  }, [matchId]);

  if (!data) return <div>Loading...</div>;
  const scoreboards = data.scoreboards || [];
  const batting = data.batting || [];
  const bowling = data.bowling || [];
  const players = data.lineup || [];
  const balls = data.balls || [];

  const getTeamName = (teamId) => {
    if (teamId === data.localteam_id) return data.localteam?.name || 'Local Team';
    if (teamId === data.visitorteam_id) return data.visitorteam?.name || 'Visitor Team';
    return `Team ${teamId}`;
  };

  const innings = [...new Set(scoreboards.map(s => s.scoreboard))];

  const getTeamIdByScoreboard = (sb) => {
    const entry = batting.find(b => b.scoreboard === sb);
    return entry?.team_id || null;
  };

  const handleTabClick = (sb) => setActiveTab(sb);

  const getPlayerName = (id) => {
    if (!id) return '';
    const player = players.find(p => p.id === id);
    return player?.fullname || 'Player undefined';
  };

  return (
    <div>
      <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
        {innings.map(sb => {
          const teamId = getTeamIdByScoreboard(sb);
          const teamName = getTeamName(teamId);
          return (
            <button
              key={sb}
              onClick={() => handleTabClick(sb)}
              style={{ fontWeight: activeTab === sb ? 'bold' : 'normal' }}
            >
              {teamName} - {sb}
            </button>
          );
        })}
      </div>

      {innings.map(sb => {
        if (sb !== activeTab) return null;

        const bat = batting.filter(b => b.scoreboard === sb);
        const bowl = bowling.filter(b => b.scoreboard === sb);
        const fow = bat.filter(b => b.fow_balls !== 0);
console.log(fow);
        const extrasObj = scoreboards.find(s => s.scoreboard === sb && s.type === 'extra');
        const totalObj = scoreboards.find(s => s.scoreboard === sb && s.type === 'total');

        const bye = extrasObj?.bye || 0;
        const legBye = extrasObj?.leg_bye || 0;
        const wide = extrasObj?.wide || 0;
        const noBall = extrasObj?.noball_runs || 0;
        const noBallBalls = extrasObj?.noball_balls || 0;
        const penalty = extrasObj?.penalty || 0;

        const totalExtras = bye + legBye + wide + noBall + penalty;

        const extrasText = `B ${bye}, LB ${legBye}, W ${wide}, NB ${noBall}, Penalty ${penalty} (Total: ${totalExtras})`;

        return (
          <div key={sb}>
            <h3>{getTeamName(getTeamIdByScoreboard(sb))} - {sb}</h3>

            <h4>Batting</h4>
            <table border="1" cellPadding="5">
              <thead>
                <tr>
                  <th>Batsman</th>
                  <th>Dismissal</th>
                  <th>Runs</th>
                  <th>Balls</th>
                  <th>4s</th>
                  <th>6s</th>
                </tr>
              </thead>
              <tbody>
                {bat.map(b => (
                  <tr key={b.id}>
                    <td>{getPlayerName(b.player_id)}</td>
                    <td>
                      {b.catch_stump_player_id && b.catch_stump_player_id !== b.bowling_player_id && (
                        <>c {getPlayerName(b.catch_stump_player_id)} b {getPlayerName(b.bowling_player_id)} </>
                      )}
                      {b.catch_stump_player_id && b.catch_stump_player_id === b.bowling_player_id && (
                        <>c & b {getPlayerName(b.bowling_player_id)}</>
                      )}
                      {b.fow_balls === 0 && (
                        <>not out</>
                      )}
                      {!b.catch_stump_player_id && b.fow_balls !== 0 && (
                        <>b {getPlayerName(b.bowling_player_id)}</>
                      )}
                    </td>
                    <td>{b.score}</td>
                    <td>{b.ball}</td>
                    <td>{b.four_x}</td>
                    <td>{b.six_x}</td>
                  </tr>
                ))}
              </tbody>
            </table>

            <p><strong>Extras:</strong> {extrasText}</p>
            <p><strong>Total:</strong> {totalObj?.total} / {totalObj?.wickets} ({totalObj?.overs} overs)</p>

            <h4>Fall of Wickets</h4>
            <ul>
              {fow.map(f => (
                <li key={f.id}>
                  {getPlayerName(f.player_id)} - {f.fow_score} ({f.fow_balls})
                </li>
              ))}
            </ul>

            <h4>Bowling</h4>
            <table border="1" cellPadding="5">
              <thead>
                <tr>
                  <th>Bowler</th>
                  <th>O</th>
                  <th>M</th>
                  <th>R</th>
                  <th>W</th>
                  <th>NB</th>
                  <th>WD</th>
                </tr>
              </thead>
              <tbody>
                {bowl.map(b => (
                  <tr key={b.id}>
                    <td>{getPlayerName(b.player_id)}</td>
                    <td>{b.overs}</td>
                    <td>{b.medians}</td>
                    <td>{b.runs}</td>
                    <td>{b.wickets}</td>
                    <td>{b.noball}</td>
                    <td>{b.wide}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        );
      })}
    </div>
  );
}
