// src/pages/Series.js
import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Series.css';

const Series = () => {
  const [seriesList, setSeriesList] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const controller = new AbortController();
    const delayDebounce = setTimeout(() => {
      const fetchSeries = async () => {
        try {
          const query = searchTerm.trim() ? `?search=${searchTerm}` : '';
          const res = await fetch(`/api/series${query}`, { signal: controller.signal });
          const json = await res.json();
          setSeriesList(json.data || []);
        } catch (err) {
          if (err.name !== 'AbortError') console.error('Error fetching series list', err);
        } finally {
          setLoading(false);
        }
      };

      fetchSeries();
    }, 400);

    return () => {
      clearTimeout(delayDebounce);
      controller.abort();
    };
  }, [searchTerm]);

  return (
    <div className="series-container">
      <h2>All Series</h2>

      <input
        type="text"
        placeholder="Search series..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="series-search"
      />

      {loading ? (
        <p>Loading series...</p>
      ) : (
        <div className="series-grid">
          {seriesList.map((s) => (
            <Link key={item.id} to={`/series/${item.id}/matches`} className="series-card">
              <h3>{s.name}</h3>
              <p>{s.startdate} - {s.enddate}</p>
              <p>{s.odi} ODIs • {s.test} Tests • {s.t20} T20s</p>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
};

export default Series;
