import React from 'react';
import './LineupTab.css';

const LineupTab = ({ match }) => {
  if (!match || !match.lineup || match.lineup.length === 0) {
    return <div>Loading lineup...</div>;
  }

  const { lineup, localteam, visitorteam } = match;
console.log(lineup);
  const localTeamPlayers = lineup.filter(p => p.team_id === localteam?.id);
  const visitorTeamPlayers = lineup.filter(p => p.team_id === visitorteam?.id);

  const renderPlayers = (players) => (
    <ul className="lineup-list">
      {players.map((p) => (
        <li key={p.id}>
          {p.fullname || `${p.firstname || ''} ${p.lastname || ''}` || `Player ID: ${p.player_id}`}
          {p.captain && <span className="player-role"> (C)</span>}
          {p.wk && <span className="player-role"> (WK)</span>}
        </li>
      ))}
    </ul>
  );

  return (
    <div className="lineup-tab">
      <div className="team-lineup">
        <h3>{localteam?.name}</h3>
        {renderPlayers(localTeamPlayers)}
      </div>
      <div className="team-lineup">
        <h3>{visitorteam?.name}</h3>
        {renderPlayers(visitorTeamPlayers)}
      </div>
    </div>
  );
};

export default LineupTab;
